import xml.etree.ElementTree as ET
import heapq
import json

def parse_map(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    grid = [['' for _ in range(width)] for _ in range(height)]
    objects_grid = [[1 for _ in range(width)] for _ in range(height)]
    player_pos = None
    for y, row_elem in enumerate(root.findall('Row')):
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            grid[y][x] = tile_elem.attrib.get('type')
            if tile_elem.find('Object') is not None:
                objects_grid[y][x] = 0
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)
    return grid, objects_grid, width, height, player_pos

def get_neighbors(coord, width, height):
    neighbors = []
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        neighbor = (coord[0] + dx, coord[1] + dy)
        if 0 <= neighbor[0] < width and 0 <= neighbor[1] < height:
            neighbors.append(neighbor)
    return neighbors

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(grid, objects_grid, start, goal, width, height):
    impassable_tiles = ['WALL', 'WINDOW', 'CUT_TREE', 'SIGN', 'BOOKSHELF', 'BLACKBOARD', 'MART_SHELF', 'BUOY', 'TV', 'TOWN_MAP', 'BIRD', 'HEADBUTT_TREE', 'FRUIT_TREE', 'COMPUTER', 'PRINTER', 'VOID', 'WATER', 'CAVE', 'COUNTER']

    goal_type = grid[goal[1]][goal[0]]
    if goal_type not in impassable_tiles and objects_grid[goal[1]][goal[0]] == 1:
        search_targets = {goal}
    else:
        search_targets = set()
        for neighbor in get_neighbors(goal, width, height):
            neighbor_type = grid[neighbor[1]][neighbor[0]]
            if neighbor_type not in impassable_tiles and objects_grid[neighbor[1]][neighbor[0]] == 1:
                search_targets.add(neighbor)

    if not search_targets:
        return None

    neighbors_diffs = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    close_set = set()
    came_from = {}
    gscore = {start: 0}
    fscore = {start: min(heuristic(start, t) for t in search_targets)}
    oheap = []
    heapq.heappush(oheap, (fscore[start], start))

    while oheap:
        current = heapq.heappop(oheap)[1]

        if current in search_targets:
            path = []
            temp = current
            while temp in came_from:
                path.append(temp)
                temp = came_from[temp]
            path.append(start)
            path.reverse()
            return path

        close_set.add(current)

        for dx, dy in neighbors_diffs:
            neighbor = (current[0] + dx, current[1] + dy)

            if not (0 <= neighbor[0] < width and 0 <= neighbor[1] < height):
                continue

            neighbor_type = grid[neighbor[1]][neighbor[0]]
            if neighbor_type in impassable_tiles or objects_grid[neighbor[1]][neighbor[0]] == 0:
                continue

            if neighbor_type == 'FLOOR_UP_WALL' and (dx, dy) != (0, -1):
                continue

            current_type = grid[current[1]][current[0]]
            if current_type == 'LEDGE_HOP_DOWN' and (dx, dy) != (0, 1): continue
            if current_type == 'LEDGE_HOP_RIGHT' and (dx, dy) != (1, 0): continue
            if current_type == 'LEDGE_HOP_LEFT' and (dx, dy) != (-1, 0): continue

            tentative_g_score = gscore[current] + 1

            if neighbor in close_set and tentative_g_score >= gscore.get(neighbor, float('inf')):
                continue

            if tentative_g_score < gscore.get(neighbor, float('inf')) or neighbor not in [i[1] for i in oheap]:
                came_from[neighbor] = current
                gscore[neighbor] = tentative_g_score
                fscore[neighbor] = tentative_g_score + min(heuristic(neighbor, t) for t in search_targets)
                heapq.heappush(oheap, (fscore[neighbor], neighbor))

    return None

