import json

target_map_name = input_data['map_name']
wkg = json.loads(world_knowledge_graph_json_string)

for node in wkg['nodes']:
    if node['map_id'] == target_map_name:
        print(f'Map ID for {target_map_name}: {node["map_id"]}')
        break
else:
    print(f'Map ID for {target_map_name} not found in World Knowledge Graph.')
