import json
import re

screen_text = input_data['screen_text']
lines = screen_text.strip().split('\n')

options = []
cursor_position = None
layout = 'single_column' # Default assumption

# Party menu check
if 'Choose a POKéMON.' in screen_text:
    pokemon_names = []
    cursor_index = -1
    for i, line in enumerate(lines):
        match = re.match(r'^([A-Z]+)', line.strip())
        if match:
            pokemon_names.append(match.group(1))
        if line.strip().startswith('→'):
            cursor_index = len(pokemon_names) - 1

    options = pokemon_names
    if cursor_index != -1:
        cursor_position = options[cursor_index]
# General menu parsing
else:
    cursor_char = '→'
    # Check for simple single-column menus first
    is_single_column = all(line.strip().startswith(cursor_char) or not re.search(r'\s{2,}', line.strip()) for line in lines if line.strip())

    if is_single_column:
        layout = 'single_column'
        for line in lines:
            if line.strip():
                option = line.strip().replace(cursor_char, '')
                options.append(option)
                if cursor_char in line:
                    cursor_position = option
    else: # Fallback to grid or more complex layouts
        layout = '2x2_grid' # Assumption for now
        all_options_flat = []
        cursor_line_index = -1
        for i, line in enumerate(lines):
            line_options = [opt.strip() for opt in re.split(r'\s{2,}', line.strip().replace(cursor_char, '')) if opt]
            all_options_flat.extend(line_options)
            if cursor_char in line:
                cursor_line_index = i

        options = all_options_flat
        if cursor_line_index != -1:
            line_with_cursor = lines[cursor_line_index]
            cursor_position = [opt.strip() for opt in re.split(r'\s{2,}', line_with_cursor.strip().replace(cursor_char, '')) if opt][0]

seen = set()
options = [x for x in options if not (x in seen or seen.add(x))]
if 'Choose' in options: options.remove('Choose')
if 'a' in options: options.remove('a')
if 'POKéMON.' in options: options.remove('POKéMON.')

result = {
    'options': options,
    'cursor_position': cursor_position,
    'layout': layout
}

print(json.dumps(result))

select_menu_option.python_script:
import json

menu_analyzer_output_str = input_data['menu_analyzer_output']
target_option = input_data['target_option']

try:
    menu_data = json.loads(menu_analyzer_output_str)
    options = menu_data.get('options', [])
    cursor_position = menu_data.get('cursor_position')
    layout = menu_data.get('layout', 'single_column')

    if not options or not cursor_position:
        print(json.dumps({"error": "Invalid menu data"}))
        exit()

    try:
        current_index = options.index(cursor_position)
        target_index = options.index(target_option)
    except ValueError:
        print(json.dumps({"error": "Option or cursor not found"}))
        exit()

    button_presses = []
    if layout == 'single_column':
        moves = target_index - current_index
        if moves > 0:
            button_presses = ['Down'] * moves
        elif moves < 0:
            button_presses = ['Up'] * abs(moves)
    elif layout == '2x2_grid':
        # Assuming a 2x2 grid with a specific layout like FIGHT, PKMN, ITEM, RUN
        positions = {options[i]: (i % 2, i // 2) for i in range(len(options))}
        current_pos = positions[cursor_position]
        target_pos = positions[target_option]

        h_moves = target_pos[0] - current_pos[0]
        v_moves = target_pos[1] - current_pos[1]

        if h_moves > 0: button_presses.extend(['Right'] * h_moves)
        if h_moves < 0: button_presses.extend(['Left'] * abs(h_moves))
        if v_moves > 0: button_presses.extend(['Down'] * v_moves)
        if v_moves < 0: button_presses.extend(['Up'] * abs(v_moves))

    print(json.dumps(button_presses))

except Exception as e:
    print(json.dumps({"error": str(e)}))

