import json
import collections
import xml.etree.ElementTree as ET

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    movement_state = input_data['movement_state']

    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            x = int(tile_elem.get('id'))
            obj = tile_elem.find('Object')
            tiles[(x, y)] = {
                'type': tile_elem.get('type'),
                'has_object': tile_elem.get('has-object') == 'true',
                'object_id': obj.get('id') if obj is not None else None
            }

    start = (start_x, start_y)
    end = (end_x, end_y)

    traversable_tile_types = {
        'ground', 'grass', 'elevated_ground', 'steps', 'ladder_up', 
        'ladder_down', 'teleport', 'hole', 'cleared_boulder_barrier',
        'open_gate', 'gate_offscreen'
    }

    if movement_state == 'surfing':
        traversable_tile_types.add('water')

    def is_traversable(x, y):
        pos = (x, y)
        if not (1 <= x <= map_width and 1 <= y <= map_height):
            return False

        tile_info = tiles.get(pos)
        if not tile_info:
            return False

        if tile_info['has_object'] and tile_info.get('object_id') != '15':
            return False

        return tile_info['type'] in traversable_tile_types

    goal_tiles = []
    if is_traversable(end_x, end_y):
        goal_tiles.append(end)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_pos = (end_x + dx, end_y + dy)
            if is_traversable(adj_pos[0], adj_pos[1]):
                goal_tiles.append(adj_pos)

    if not goal_tiles:
        end_tile_type = tiles.get(end, {}).get('type', 'unknown')
        print(json.dumps({'success': False, 'reason': f'Destination ({end_x}, {end_y}) and all adjacent tiles are impassable. Destination tile type: {end_tile_type}.', 'path_plan': []}))
        exit()

    queue = collections.deque([start])
    came_from = {start: None}
    path_found = False
    found_dest = None

    while queue:
        current_pos = queue.popleft()

        if current_pos in goal_tiles:
            found_dest = current_pos
            path_found = True
            break

        (cx, cy) = current_pos
        # West, Down, Up, Right
        directions = [(-1, 0), (0, 1), (0, -1), (1, 0)]
        for dx, dy in directions:
            next_pos = (cx + dx, cy + dy)

            if next_pos in came_from:
                continue

            if tiles.get(next_pos, {}).get('type') == 'ledge':
                if dy == 1: # Moving down onto a ledge
                    final_pos = (cx, cy + 2)
                    if final_pos not in came_from and is_traversable(final_pos[0], final_pos[1]):
                         came_from[final_pos] = current_pos
                         queue.append(final_pos)
                continue

            if is_traversable(next_pos[0], next_pos[1]):
                came_from[next_pos] = current_pos
                queue.append(next_pos)

    if path_found:
        path = []
        curr = found_dest
        while curr is not None:
            path.append(curr)
            curr = came_from.get(curr)
        path.reverse()

        final_path = [{'x': p[0], 'y': p[1]} for p in path]
        print(json.dumps({'success': True, 'reason': 'Path found.', 'path_plan': final_path}))
    else:
        closest_tile = None
        min_dist = float('inf')
        obstacle_info = 'Unknown obstacle.'
        # Find the tile in came_from closest to the destination
        for tile in came_from.keys():
            dist = abs(tile[0] - end_x) + abs(tile[1] - end_y)
            if dist < min_dist:
                min_dist = dist
                closest_tile = tile

        if closest_tile:
            (ctx, cty) = closest_tile
            # Check neighbors of the closest tile to find the blocker
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                check_pos = (ctx + dx, cty + dy)
                if not is_traversable(check_pos[0], check_pos[1]):
                    obstacle_tile = tiles.get(check_pos)
                    if obstacle_tile:
                        obstacle_type = obstacle_tile.get('type', 'unknown')
                        obstacle_info = f"Path blocked by impassable tile of type '{obstacle_type}' at {check_pos}."
                        break

        reason = f'No path found. The destination is in a separate, unreachable map partition. {obstacle_info}'
        print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))

except Exception as e:
    print(json.dumps({'success': False, 'reason': f'An error occurred: {str(e)}', 'path_plan': []}))

