import json

try:
    menu_data = json.loads(input_data['menu_analyzer_output'])
    target_pokemon = input_data['target_pokemon']

    options = menu_data['options']
    cursor_position = menu_data['cursor_position']

    if target_pokemon not in options or cursor_position not in options:
        print(json.dumps([]))
    else:
        current_index = options.index(cursor_position)
        target_index = options.index(target_pokemon)

        diff = target_index - current_index

        buttons = []
        if diff > 0:
            buttons = ['Down'] * diff
        elif diff < 0:
            buttons = ['Up'] * abs(diff)

        print(json.dumps(buttons))

except Exception as e:
    print(json.dumps([]))
