import json
import collections

def find_route(graph, start_map_id, end_map_id):
    if start_map_id not in graph or end_map_id not in graph:
        return None

    queue = collections.deque([[start_map_id]])
    visited = {start_map_id}

    while queue:
        path = queue.popleft()
        node = path[-1]

        if node == end_map_id:
            return path

        for neighbor in graph.get(node, []):
            if neighbor not in visited:
                visited.add(neighbor)
                new_path = list(path)
                new_path.append(neighbor)
                queue.append(new_path)

    return None

try:
    data = json.loads(world_knowledge_graph_json_string)
    start_map = input_data['start_map_id']
    end_map = input_data['end_map_id']

    adjacency_list = collections.defaultdict(list)
    for edge in data['edges']:
        source_map = edge['source_coordinates']['map_id']
        dest_map = edge['destination_coordinates']['map_id']
        adjacency_list[source_map].append(dest_map)
        if not edge.get('is_one_way', False):
            adjacency_list[dest_map].append(source_map)

    route = find_route(adjacency_list, start_map, end_map)
    print(json.dumps({"route": route}))

except Exception as e:
    print(json.dumps({"error": str(e)}))

