import xml.etree.ElementTree as ET
import json

try:
    root = ET.fromstring(map_xml_string)
    farfetchd_pos = None
    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            obj = tile.find('Object')
            if obj is not None and obj.get('id-name') == 'FARFETCHD':
                farfetchd_pos = (int(tile.get('id')), int(row.get('id')))
                break
        if farfetchd_pos:
            break

    if farfetchd_pos:
        print(json.dumps({"status": "found", "farfetchd_x": farfetchd_pos[0], "farfetchd_y": farfetchd_pos[1], "message": "Farfetch'd found. Herding logic not yet implemented."}))
    else:
        print(json.dumps({"status": "not_found", "message": "Farfetch'd object not found on the current map."}))

except Exception as e:
    import traceback
    print(json.dumps({"status": "error", "message": f"An exception occurred: {str(e)}\n{traceback.format_exc()}"}))
