import json
def script(input_data):
    x1, y1 = int(input_data['x1']), int(input_data['y1'])
    x2, y2 = int(input_data['x2']), int(input_data['y2'])
    min_x, max_x = min(x1, x2), max(x1, x2)
    min_y, max_y = min(y1, y2), max(y1, y2)
    path = []
    for y in range(min_y, max_y + 1):
        if (y - min_y) % 2 == 0:
            for x in range(min_x, max_x + 1):
                path.append({"x": x, "y": y})
        else:
            for x in range(max_x, min_x - 1, -1):
                path.append({"x": x, "y": y})
    print(json.dumps(path))
