import xml.etree.ElementTree as ET; from collections import deque; import json

def find_path(target_x, target_y):
    root = ET.fromstring(map_xml_string)
    try:
        target_x, target_y = int(target_x), int(target_y)
    except (ValueError, TypeError):
        print(json.dumps([]))
        return

    IMPASSABLE_TILE_TYPES = ['WALL', 'WATER', 'COUNTER', 'HEADBUTT_TREE', 'CUT_TREE', 'PC', 'MART_SHELF', 'VOID', 'BUOY', 'WINDOW', 'BOOKSHELF', 'BLACKBOARD', 'TOWN_MAP', 'RADIO', 'INCENSE_BURNER', 'BIRD', 'FRUIT_TREE', 'CAVE']
    ONE_WAY_RULES = {
        'LEDGE_HOP_DOWN': (0, 1),
        'LEDGE_HOP_RIGHT': (1, 0),
        'LEDGE_HOP_LEFT': (-1, 0)
    }

    grid = {}
    start_x, start_y = -1, -1

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            markers = [marker.get('emoji') for marker in tile.findall('Marker')]
            grid[(x, y)] = {'type': tile.get('type'), 'has_object': tile.get('has-object') == 'true', 'markers': markers}
            if tile.find('Player') is not None:
                start_x, start_y = x, y

    if start_x == -1:
        print(json.dumps([]))
        return

    def is_passable(x, y):
        tile_info = grid.get((x, y))
        if not tile_info: return False
        if tile_info.get('type') in IMPASSABLE_TILE_TYPES: return False
        if tile_info.get('has_object'):
            if '✅' in tile_info.get('markers', []): return True 
            return False
        return True

    destination_tiles = set()
    if is_passable(target_x, target_y):
        destination_tiles.add((target_x, target_y))
    else:
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            adj_x, adj_y = target_x + dx, target_y + dy
            if is_passable(adj_x, adj_y):
                destination_tiles.add((adj_x, adj_y))

    if not destination_tiles:
        print(json.dumps([]))
        return

    queue = deque([[(start_x, start_y)]])
    visited = {(start_x, start_y)}

    while queue:
        path = queue.popleft()
        current_x, current_y = path[-1]

        if (current_x, current_y) in destination_tiles:
            print(json.dumps(path))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = current_x + dx, current_y + dy
            neighbor_coord = (next_x, next_y)

            if neighbor_coord in visited:
                continue

            if not is_passable(next_x, next_y):
                continue

            can_move = True
            current_type = grid.get((current_x, current_y), {}).get('type')
            neighbor_type = grid.get(neighbor_coord, {}).get('type')

            if current_type == 'FLOOR_UP_WALL' and dy == -1:
                can_move = False

            if neighbor_type == 'FLOOR_UP_WALL' and dy == 1:
                can_move = False

            if neighbor_type in ONE_WAY_RULES:
                allowed_entry_dx, allowed_entry_dy = ONE_WAY_RULES[neighbor_type]
                if (dx, dy) != (allowed_entry_dx, allowed_entry_dy):
                    can_move = False

            if can_move:
                new_path = list(path)
                new_path.append(neighbor_coord)
                visited.add(neighbor_coord)
                queue.append(new_path)

    print(json.dumps([]))

