import heapq
import xml.etree.ElementTree as ET

def heuristic(x, y, target_x, target_y):
    return abs(x - target_x) + abs(y - target_y)

def pathfinder(input_data, map_xml_string):
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        return f'Error parsing map XML: {e}'

    player_pos = None
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.find('Player') is not None:
                player_pos = (int(tile_elem.get('id')), int(row_elem.get('id')))
                break
        if player_pos:
            break

    if not player_pos:
        return 'Player position not found on the map.'

    start_x, start_y = player_pos
    start_node = (start_x, start_y)

    tile_map = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_obj = tile_elem.find('Object') is not None
            obj_is_pikachu = False
            if has_obj:
                obj_elem = tile_elem.find('Object')
                if obj_elem.get('id-name') == 'Pikachu':
                    obj_is_pikachu = True
            tile_map[(x, y)] = {'type': tile_type, 'has_obj': has_obj, 'obj_is_pikachu': obj_is_pikachu}

    def is_walkable(x, y, on_water):
        if (x, y) not in tile_map:
            return False
        tile_data = tile_map[(x, y)]
        tile_type = tile_data['type']

        if tile_data['has_obj'] and not tile_data['obj_is_pikachu']:
            return False

        walkable_ground = ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'teleport', 'ladder_up', 'ladder_down', 'spinner_stop', 'spinner_up', 'spinner_down', 'spinner_left', 'spinner_right']

        if on_water:
            return tile_type == 'water'
        else:
            return tile_type in walkable_ground

    open_set = [(heuristic(start_x, start_y, target_x, target_y), 0, start_node, False, [])]
    closed_set = set()

    is_target_walkable_land = is_walkable(target_x, target_y, False)
    is_target_walkable_water = is_walkable(target_x, target_y, True)
    is_target_impassable = not is_target_walkable_land and not is_target_walkable_water

    while open_set:
        _, cost, current, on_water, path = heapq.heappop(open_set)

        if (current, on_water) in closed_set:
            continue

        closed_set.add((current, on_water))
        new_path = path + [current]

        if current == (target_x, target_y):
            return new_path

        if is_target_impassable and (abs(current[0] - target_x) + abs(current[1] - target_y) == 1):
            if is_walkable(current[0], current[1], on_water):
                 return new_path

        x, y = current
        for dx, dy, move in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            next_x, next_y = x + dx, y + dy

            if (dx, dy) == (0, 1) and (next_x, next_y) in tile_map and tile_map[(next_x, next_y)]['type'] == 'ledge':
                next_y += 1

            next_on_water = on_water

            if (next_x, next_y) not in tile_map:
                continue

            tile_type = tile_map[(next_x, next_y)]['type']

            if tile_type == 'ledge' and (dx, dy) != (0, 1):
                continue

            if tile_type == 'water' and not on_water:
                next_on_water = True
            elif tile_type != 'water' and on_water:
                if tile_map[current]['type'] == 'water':
                    next_on_water = False
                else:
                    continue

            if is_walkable(next_x, next_y, next_on_water):
                if ((next_x, next_y), next_on_water) not in closed_set:
                    new_cost = cost + 1
                    priority = new_cost + heuristic(next_x, next_y, target_x, target_y)
                    heapq.heappush(open_set, (priority, new_cost, (next_x, next_y), next_on_water, new_path))

    return 'No path found.'

print(pathfinder(input_data, map_xml_string))

