import heapq
import xml.etree.ElementTree as ET

def spinner_maze_solver(input_data, map_xml_string):
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        return f'Error parsing map XML: {e}'

    player_pos = None
    tile_map = {}
    spinners = {}

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)

            tile_type = tile_elem.get('type')
            has_obj = tile_elem.find('Object') is not None
            obj_is_pikachu = False
            if has_obj:
                obj_elem = tile_elem.find('Object')
                if obj_elem.get('id-name') == 'Pikachu':
                    obj_is_pikachu = True

            tile_map[(x, y)] = {'type': tile_type, 'has_obj': has_obj, 'obj_is_pikachu': obj_is_pikachu}

            spinner_elem = tile_elem.find('Spinner')
            if spinner_elem is not None and 'end-coordinate' in spinner_elem.attrib:
                end_coord_str = spinner_elem.get('end-coordinate')
                end_x, end_y = map(int, end_coord_str.split(','))
                spinners[(x, y)] = (end_x, end_y)

    if not player_pos:
        return 'Player position not found.'

    start_node = player_pos

    def is_walkable(x, y):
        if (x, y) not in tile_map: return False
        tile = tile_map[(x, y)]
        return not (tile['has_obj'] and not tile['obj_is_pikachu']) and tile['type'] not in ['impassable']

    def get_neighbors(pos):
        x, y = pos
        neighbors = []
        # Standard moves
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_pos = (x + dx, y + dy)
            if is_walkable(next_pos[0], next_pos[1]):
                 # If next tile is a spinner, the only valid move is to the spinner's destination
                if next_pos in spinners:
                    neighbors.append((spinners[next_pos], 1)) # Cost of 1 to step on spinner
                else:
                    neighbors.append((next_pos, 1))
        return neighbors

    # A* algorithm
    open_set = [(0, start_node)]
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: abs(start_node[0] - target_x) + abs(start_node[1] - target_y)}

    while open_set:
        _, current = heapq.heappop(open_set)

        if abs(current[0] - target_x) + abs(current[1] - target_y) <= 1 and is_walkable(current[0], current[1]):
             path = []
             while current in came_from:
                 path.append(current)
                 current = came_from[current]
             path.append(start_node)
             return path[::-1]

        for neighbor, cost in get_neighbors(current):
            tentative_g_score = g_score.get(current, float('inf')) + cost
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + abs(neighbor[0] - target_x) + abs(neighbor[1] - target_y)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    return 'No path found.'

print(spinner_maze_solver(input_data, map_xml_string))

