import json
import xml.etree.ElementTree as ET
from collections import deque

def find_path(map_xml, start, end, avoid_warps):
    root = ET.fromstring(map_xml)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            has_obj = tile.attrib.get('has-object') == 'true'
            is_warp = tile.attrib.get('is-warp') == 'true'

            passable = True
            if t_type in ["WALL", "BUOY", "CUT_TREE", "BOULDER", "COUNTER", "VOID"]:
                passable = False
            if has_obj:
                passable = False
            if avoid_warps and is_warp and (x, y) != end:
                passable = False

            grid[(x, y)] = passable

    queue = deque([(start, [])])
    visited = {start}

    while queue:
        (curr_x, curr_y), path = queue.popleft()
        if (curr_x, curr_y) == end:
            return path

        for dx, dy, btn in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
            nx, ny = curr_x + dx, curr_y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                if grid.get((nx, ny), False) or (nx, ny) == end:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [btn]))
    return None

