import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
    except (KeyError, ValueError, TypeError):
        print(json.dumps([]))
        return

    try:
        root = ET.fromstring(map_xml_string)
    except Exception:
        print(json.dumps([]))
        return

    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    matrix = [[None for _ in range(width)] for _ in range(height)]
    player_pos = None

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            is_warp = tile.attrib.get('is-warp') == 'true'
            has_player = tile.find('Player') is not None
            has_object = tile.find('Object') is not None

            matrix[y][x] = {
                'type': t_type,
                'is_warp': is_warp,
                'has_object': has_object
            }
            if has_player:
                player_pos = (x, y)

    if not player_pos:
        print(json.dumps([]))
        return

    start_x, start_y = player_pos

    # BFS
    queue = deque([(start_x, start_y, [])])
    visited = set([(start_x, start_y)])

    impassable = ["WALL", "BUOY", "ROCK", "BOOKSHELF", "TV", "RADIO", "TOWN_MAP", "STATUE", "COUNTER", "HEADBUTT_TREE", "CUT_TREE", "WHIRLPOOL"]

    while queue:
        cx, cy, path = queue.popleft()

        if cx == target_x and cy == target_y:
            print(json.dumps(path))
            return

        current_tile = matrix[cy][cx]

        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = cx + dx, cy + dy

            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                neighbor_tile = matrix[ny][nx]

                # Basic collision
                if neighbor_tile['type'] in impassable:
                    continue

                # Objects, Warps, and Pits are walls unless they are the target
                if (neighbor_tile['has_object'] or neighbor_tile['is_warp'] or neighbor_tile['type'] == "PIT") and (nx != target_x or ny != target_y):
                    continue

                # Movement rules
                can_move = False
                if current_tile['type'] == "WATER":
                    # If surfing, can move to water or land (FLOOR)
                    if neighbor_tile['type'] in ["WATER", "FLOOR"]:
                        can_move = True
                else:
                    # If walking, can only move to land
                    if neighbor_tile['type'] == "FLOOR":
                        can_move = True

                if can_move:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))

    print(json.dumps([]))

solve()
