import json
import re

screen_text = input_data['screen_text']
lines = [line.strip() for line in screen_text.strip().split('\n') if line.strip()]

options = []
cursor_position = None
layout = 'single_column'
cursor_char = '→'

# Special handler for the glitched party sub-menu
submenu_keywords = ['STATS', 'SWITCH', 'CANCEL']
is_submenu = all(keyword in screen_text for keyword in submenu_keywords)

if is_submenu:
    layout = 'single_column'
    options = submenu_keywords

    # The text contains a rendering artifact ('→FLASH') making direct cursor parsing unreliable.
    # The game's cursor defaults to the first option, 'STATS', when this menu opens.
    # We will assume this default unless the cursor is explicitly next to another valid option.
    found_cursor = False
    for line in lines:
        if cursor_char in line:
            # Check if this line contains one of our known valid options
            for opt in options:
                if opt in line:
                    # The cursor is next to a valid option, so we can trust it
                    cursor_position = opt
                    found_cursor = True
                    break
        if found_cursor:
            break

    # If the cursor was found on a line with junk text, assume it's on the first option
    if not found_cursor and options:
        cursor_position = options[0]

else:
    # General menu parsing logic
    is_party_menu = "Choose a POKéMON" in screen_text or "Bring out which POKéMON" in screen_text
    if is_party_menu:
        pokemon_names = []
        cursor_index = -1
        text_lines = screen_text.strip().split('\n')
        for i, line in enumerate(text_lines):
            match = re.match(r"^\s*([A-Z']{2,})\s+LV", line)
            if match:
                pokemon_name = match.group(1).strip()
                pokemon_names.append(pokemon_name)
                if i + 1 < len(text_lines) and text_lines[i+1].strip().startswith(cursor_char):
                    cursor_index = len(pokemon_names) - 1
        options = pokemon_names
        if cursor_index != -1 and options:
            cursor_position = options[cursor_index]
    else:
        is_multi_column = any(re.search(r'\S\s{2,}\S', line) for line in lines)
        if is_multi_column:
            layout = '2x2_grid'
            flat_options = []
            cursor_opt = None
            for line in lines:
                parts = re.split(r'\s{2,}', line)
                for part in parts:
                    if part:
                        clean_part = part.replace(cursor_char, '')
                        flat_options.append(clean_part)
                        if cursor_char in part:
                            cursor_opt = clean_part
            options = flat_options
            cursor_position = cursor_opt
        else:
            for line in lines:
                option = line.replace(cursor_char, '')
                options.append(option)
                if cursor_char in line:
                    cursor_position = option

result = {
    'options': options,
    'cursor_position': cursor_position,
    'layout': layout
}
print(json.dumps(result))

select_menu_option.python_script:
import json
import collections

menu_analyzer_output = input_data['menu_analyzer_output']
target_option = input_data['target_option']
confirm_selection = input_data.get('confirm_selection', 'false').lower() == 'true'

data = json.loads(menu_analyzer_output)
options = data.get('options', [])
cursor_position = data.get('cursor_position')
layout = data.get('layout', 'single_column')

button_presses = []

if not options or cursor_position is None or target_option not in options:
    print(json.dumps([]))
else:
    try:
        start_index = options.index(cursor_position)
        target_index = options.index(target_option)

        if layout == 'single_column':
            diff = target_index - start_index
            if diff > 0:
                button_presses.extend(['Down'] * diff)
            elif diff < 0:
                button_presses.extend(['Up'] * abs(diff))

        elif layout == '2x2_grid':
            rows, cols = 2, 2
            start_row, start_col = divmod(start_index, cols)
            target_row, target_col = divmod(target_index, cols)

            row_diff = target_row - start_row
            col_diff = target_col - start_col

            if row_diff > 0:
                button_presses.extend(['Down'] * row_diff)
            elif row_diff < 0:
                button_presses.extend(['Up'] * abs(row_diff))

            if col_diff > 0:
                button_presses.extend(['Right'] * col_diff)
            elif col_diff < 0:
                button_presses.extend(['Left'] * abs(col_diff))

        if confirm_selection:
            button_presses.append('A')

        print(json.dumps(button_presses))

    except (ValueError, KeyError) as e:
        print(json.dumps([]))

automated_path_navigator.python_script:
import json
import collections
import xml.etree.ElementTree as ET

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    movement_state = input_data['movement_state']

    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            x = int(tile_elem.get('id'))
            obj = tile_elem.find('Object')
            tiles[(x, y)] = {
                'type': tile_elem.get('type'),
                'has_object': tile_elem.get('has-object') == 'true',
                'object_id_name': obj.get('id-name') if obj is not None else None
            }

    start = (start_x, start_y)
    end = (end_x, end_y)

    base_traversable_tile_types = {
        'ground', 'elevated_ground', 'steps', 'ladder_up', 
        'ladder_down', 'teleport', 'hole', 'cleared_boulder_barrier',
        'open_gate', 'gate_offscreen'
    }

    if movement_state == 'surfing':
        base_traversable_tile_types.add('water')

    def is_valid_move(pos, from_pos=None):
        x, y = pos
        if not (1 <= x <= map_width and 1 <= y <= map_height):
            return False

        tile_info = tiles.get(pos)
        if not tile_info or tile_info['type'] not in base_traversable_tile_types:
             return False

        if tile_info['has_object'] and (tile_info.get('object_id_name') or '').upper() != 'PIKACHU':
            return False

        if from_pos:
            current_tile_info = tiles.get(from_pos)
            current_type = current_tile_info.get('type')
            next_type = tile_info.get('type')

            is_elevation_change = (current_type == 'ground' and next_type == 'elevated_ground') or \
                                  (current_type == 'elevated_ground' and next_type == 'ground')

            is_via_steps = (current_type == 'steps' or next_type == 'steps')

            if is_elevation_change and not is_via_steps:
                return False

        return True

    goal_tiles = []
    if is_valid_move(end):
        goal_tiles.append(end)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_pos = (end_x + dx, end_y + dy)
            if is_valid_move(adj_pos):
                goal_tiles.append(adj_pos)

    if not goal_tiles:
        end_tile_info = tiles.get(end)
        reason = f'Destination ({end_x}, {end_y}) and all adjacent tiles are impassable.'
        if end_tile_info:
            if end_tile_info.get('type') == 'water' and movement_state == 'walking':
                reason = 'Path blocked by water, requires Surf.'
            elif end_tile_info.get('has_object') and (end_tile_info.get('object_id_name') or '').upper() != 'PIKACHU':
                reason += f" Destination blocked by object: {end_tile_info['object_id_name']}."
            else:
                reason += f" Destination tile type: {end_tile_info.get('type', 'unknown')}."
        print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))
        exit()

    queue = collections.deque([(start, [start])])
    visited = {start}
    path_found = False
    final_path_coords = []

    while queue:
        current_pos, path = queue.popleft()

        if current_pos in goal_tiles:
            final_path_coords = path
            path_found = True
            break

        (cx, cy) = current_pos
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            next_pos = (cx + dx, cy + dy)

            if next_pos in visited:
                continue

            if tiles.get(next_pos, {}).get('type') == 'ledge':
                if dy == 1 and dx == 0:
                    final_pos = (cx, cy + 2)
                    if final_pos not in visited and is_valid_move(final_pos, from_pos=current_pos):
                        visited.add(final_pos)
                        new_path = list(path)
                        new_path.append(final_pos)
                        queue.append((final_pos, new_path))
                continue

            if is_valid_move(next_pos, from_pos=current_pos):
                visited.add(next_pos)
                new_path = list(path)
                new_path.append(next_pos)
                queue.append((next_pos, new_path))

    if path_found:
        final_path_plan = [{'x': p[0], 'y': p[1]} for p in final_path_coords]
        print(json.dumps({'success': True, 'reason': 'Path found.', 'path_plan': final_path_plan}))
    else:
        # Full reachability check from start to diagnose partitions
        reachable_nodes = set()
        q = collections.deque([start])
        reachable_nodes.add(start)
        while q:
            pos = q.popleft()
            (x, y) = pos
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                next_node = (x + dx, y + dy)
                if next_node in reachable_nodes:
                    continue

                if tiles.get(next_node, {}).get('type') == 'ledge':
                    if dy == 1 and dx == 0:
                        final_node = (x, y + 2)
                        if final_node not in reachable_nodes and is_valid_move(final_node, from_pos=pos):
                            reachable_nodes.add(final_node)
                            q.append(final_node)
                    continue

                if is_valid_move(next_node, from_pos=pos):
                    reachable_nodes.add(next_node)
                    q.append(next_node)

        is_dest_reachable = any(goal in reachable_nodes for goal in goal_tiles)
        if not is_dest_reachable:
            reason = 'No path found. The destination is in a separate, unreachable map partition.'
        else:
            reason = 'Pathfinding error: Destination is reachable, but BFS failed to find a path. Check algorithm logic.'

        print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))

except Exception as e:
    print(json.dumps({'success': False, 'reason': f'An error occurred: {str(e)}', 'path_plan': []}))
