import json

try:
    # Argument Handling Logic
    args = locals()
    axis = 'horizontal' # Default value

    # Extract axis from locals or input_data
    if 'axis' in args:
        axis = args['axis']
    elif 'input_data' in args and isinstance(args['input_data'], dict) and 'axis' in args['input_data']:
        axis = args['input_data']['axis']

    buttons = []
    if axis == 'vertical':
        seq = ["Up", "Down"]
    else:
        # Default to horizontal if unknown or 'horizontal'
        seq = ["Left", "Right"]

    # Generate sequence: 10 repetitions of the pair (20 steps total)
    for _ in range(10):
        buttons.extend(seq)

    print(json.dumps(buttons))

except Exception as e:
    # Fallback to a safe default movement to avoid crashing
    print(json.dumps(["Left", "Right"]))
