import xml.etree.ElementTree as ET
from collections import deque
import json

def bfs(start_pos, target_pos, walls, width, height):
    queue = deque([[start_pos]])
    visited = set([start_pos])
    while queue:
        path = queue.popleft()
        x, y = path[-1]
        if (x, y) == target_pos:
            return path
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in walls and (nx, ny) not in visited:
                visited.add((nx, ny))
                new_path = list(path)
                new_path.append((nx, ny))
                queue.append(new_path)
    return None

def main():
    try:
        root = ET.fromstring(map_xml_string)
    except Exception as e:
        print(f"Error parsing XML: {e}")
        return

    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    walls = set()
    player_pos = None

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']

            has_object = False
            for child in tile:
                if child.tag == 'Object':
                    has_object = True
                if child.tag == 'Player':
                    player_pos = (x, y)
                # Also treat markers with specific emojis as walls if needed, 
                # but generally we rely on the map data. 
                # For now, let's trust the 'type' and 'Object' tags.
                # If you want to block tiles with specific markers (like 'Shutter Closed'),
                # you can iterate through markers here.
                for marker in tile.findall('Marker'):
                    if "Shutter Closed" in marker.text or "Closed" in marker.text:
                        walls.add((x, y))

            if t_type == 'WALL' or has_object:
                walls.add((x, y))

    # Handle inputs
    sx = globals().get('start_x')
    sy = globals().get('start_y')
    tx = globals().get('target_x')
    ty = globals().get('target_y')
    fmt = globals().get('output_format')
    auto = globals().get('autopress_buttons', False)

    # If not provided, try to find player position
    if sx is None or sy is None:
        if player_pos:
            sx, sy = player_pos
        else:
            # If we can't find the player, we can't pathfind from "current position"
            print(json.dumps([])) # Return empty list on error
            return

    # Default output format logic
    if not fmt:
        if auto:
            fmt = 'buttons'
        else:
            fmt = 'coords'

    start = (sx, sy)
    target = (tx, ty)

    path = bfs(start, target, walls, width, height)

    if path:
        if fmt == 'buttons':
            buttons = []
            current_x, current_y = start
            for i in range(1, len(path)):
                next_x, next_y = path[i]
                if next_x > current_x: buttons.append("Right")
                elif next_x < current_x: buttons.append("Left")
                elif next_y > current_y: buttons.append("Down")
                elif next_y < current_y: buttons.append("Up")
                current_x, current_y = next_x, next_y
            print(json.dumps(buttons))
        else:
            coords = [{"x": p[0], "y": p[1]} for p in path]
            print(json.dumps(coords))
    else:
        # RETURN EMPTY LIST INSTEAD OF TEXT TO PREVENT PARSING ERRORS
        print(json.dumps([]))

if __name__ == "__main__":
    main()
