import json
import xml.etree.ElementTree as ET
import heapq

def find_path():
    try:
        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        end_x = int(input_data['end_x'])
        end_y = int(input_data['end_y'])
    except (KeyError, ValueError) as e:
        print(json.dumps({"error": f"Invalid or missing input parameters: {e}"}))
        return

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        print(json.dumps({"error": f"Failed to parse map XML: {e}"}))
        return

    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = [['' for _ in range(height + 1)] for _ in range(width + 1)]

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            if tile_elem.find('Object') is not None:
                if (x, y) != (start_x, start_y) and (x, y) != (end_x, end_y):
                    grid[x][y] = 'WALL'
                else:
                    grid[x][y] = tile_type
            else:
                grid[x][y] = tile_type

    traversable = ['FLOOR', 'WARP_CARPET_DOWN']
    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    if not (1 <= start_x <= width and 1 <= start_y <= height and 1 <= end_x <= width and 1 <= end_y <= height):
        print(json.dumps({"error": "Start or end coordinates are out of map bounds."}))
        return

    if grid[start_x][start_y] not in traversable or grid[end_x][end_y] not in traversable:
        print(json.dumps({"error": "Start or end tile is not traversable."}))
        return

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (0, start_node))

    came_from = {}
    g_score = { (x, y): float('inf') for x in range(width + 1) for y in range(height + 1) }
    g_score[start_node] = 0

    f_score = { (x, y): float('inf') for x in range(width + 1) for y in range(height + 1) }
    f_score[start_node] = heuristic(start_node, end_node)

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end_node:
            path = []
            while current in came_from:
                path.append({'x': current[0], 'y': current[1]})
                current = came_from[current]
            path.append({'x': start_node[0], 'y': start_node[1]})
            print(json.dumps(path[::-1]))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            nx, ny = neighbor

            if not (1 <= nx <= width and 1 <= ny <= height):
                continue

            if grid[nx][ny] not in traversable:
                continue

            tentative_g_score = g_score[current] + 1

            if tentative_g_score < g_score[neighbor]:
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = g_score[neighbor] + heuristic(neighbor, end_node)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    print(json.dumps({"error": "No path found."}))

find_path()
