import xml.etree.ElementTree as ET
from collections import deque

def is_walkable(tile, tiles_map):
    # Basic collision logic
    t_type = tile.get('type', 'FLOOR')
    if t_type in ['WALL', 'HEADBUTT_TREE', 'CUT_TREE', 'WATER', 'PC', 'COUNTER', 'WINDOW', 'BOOKSHELF', 'INCENSE_BURNER', 'STATUE']:
        return False
    # Check for objects (unless it's a door/warp which might be walkable)
    if tile.get('has-object') == 'true' and not tile.get('is-warp') == 'true':
         return False
    return True

def scan_exploration_targets(map_xml_string):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    tiles = {}
    player_pos = (0, 0)

    # Parse map
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tiles[(x, y)] = tile
            if tile.get('has-player') == 'true':
                player_pos = (x, y)

    # BFS
    queue = deque([(player_pos, 0)])
    visited = {player_pos}
    reachable_unseen = []

    while queue:
        (cx, cy), dist = queue.popleft()

        # Check neighbors
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy

            if (nx, ny) in tiles:
                neighbor = tiles[(nx, ny)]
                seen = neighbor.get('seen')

                # If we found an edge of exploration
                if seen == 'false':
                    if (nx, ny) not in [x[0] for x in reachable_unseen]:
                        reachable_unseen.append(((nx, ny), dist + 1))
                    continue # Don't traverse unseen tiles, just mark them

                if (nx, ny) not in visited:
                    if is_walkable(neighbor, tiles):
                        visited.add((nx, ny))
                        queue.append(((nx, ny), dist + 1))

    # Sort by distance
    reachable_unseen.sort(key=lambda x: x[1])

    return [x[0] for x in reachable_unseen]

print(scan_exploration_targets(map_xml_string))
