import xml.etree.ElementTree as ET
import json
from collections import deque

def solve():
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    matrix = [[None for _ in range(width)] for _ in range(height)]
    player_pos = None

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib['type']
            seen = tile.attrib['seen'] == 'true'
            has_object = tile.attrib.get('has-object') == 'true'

            matrix[y][x] = {
                'type': tile_type,
                'seen': seen,
                'has_object': has_object
            }

            if tile.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        print(json.dumps([]))
        return

    def is_passable(x, y, from_x, from_y):
        if not (0 <= x < width and 0 <= y < height):
            return False
        tile = matrix[y][x]
        if not tile['seen']:
            return False
        if tile['has_object']:
            return False

        t_type = tile['type']
        f_type = matrix[from_y][from_x]['type']

        # Basic wall check
        if t_type in ["WALL", "FLOOR_UP_WALL", "WATER", "ROCK", "INCENSE_BURNER", "COUNTER", "HEADBUTT_TREE", "CUT_TREE"]:
            return False

        # Ledge logic
        if f_type == "LEDGE_HOP_DOWN" and y != from_y + 2: return False
        if f_type == "LEDGE_HOP_RIGHT" and x != from_x + 2: return False
        if f_type == "LEDGE_HOP_LEFT" and x != from_x - 2: return False

        if t_type == "LEDGE_HOP_DOWN" and y != from_y + 1: return False
        if t_type == "LEDGE_HOP_RIGHT" and x != from_x + 1: return False

        return True

    reachable_unseen = []
    queue = deque([player_pos])
    visited = {player_pos}
    parent = {player_pos: None}

    unseen_coords = []

    while queue:
        curr_x, curr_y = queue.popleft()

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy

            if 0 <= nx < width and 0 <= ny < height:
                if not matrix[ny][nx]['seen']:
                    if (nx, ny) not in unseen_coords:
                        unseen_coords.append((nx, ny))
                    continue

                if (nx, ny) not in visited and is_passable(nx, ny, curr_x, curr_y):
                    visited.add((nx, ny))
                    parent[(nx, ny)] = (curr_x, curr_y)
                    queue.append((nx, ny))

    if input_data.get('autopress_buttons'):
        if not unseen_coords:
            print(json.dumps([]))
            return

        # Target the first unseen tile's parent (the traversable tile next to it)
        # and return buttons to that tile, then one final press towards the unseen.
        # For simplicity in this fix, I'll just return the coords as requested by the LLM 
        # but the system error was about the type.
        # If the LLM wants buttons, it needs a path.

        # Find path to the first reachable point adjacent to an unseen tile
        target_unseen = unseen_coords[0]
        # We already found the path to 'curr_x, curr_y' which is adjacent to 'nx, ny'
        # Let's find which visited tile is adjacent to target_unseen
        best_neighbor = None
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            px, py = target_unseen[0] - dx, target_unseen[1] - dy
            if (px, py) in visited:
                best_neighbor = (px, py)
                break

        if not best_neighbor:
            print(json.dumps([]))
            return

        path = []
        curr = best_neighbor
        while curr:
            path.append(curr)
            curr = parent[curr]
        path.reverse()

        buttons = []
        for i in range(len(path) - 1):
            x1, y1 = path[i]
            x2, y2 = path[i+1]
            if x2 > x1: buttons.append("Right")
            elif x2 < x1: buttons.append("Left")
            elif y2 > y1: buttons.append("Down")
            elif y2 < y1: buttons.append("Up")

        # Final move towards unseen
        x1, y1 = best_neighbor
        x2, y2 = target_unseen
        if x2 > x1: buttons.append("Right")
        elif x2 < x1: buttons.append("Left")
        elif y2 > y1: buttons.append("Down")
        elif y2 < y1: buttons.append("Up")

        print(json.dumps(buttons))
    else:
        print(json.dumps(unseen_coords))

solve()
