import json
import xml.etree.ElementTree as ET

def find_unmarked_tiles(xml_string, x_min, x_max, y_min, y_max, emoji):
    root = ET.fromstring(xml_string)
    unmarked = []
    for row in root.findall('Row'):
        y = int(row.get('id'))
        if y_min <= y <= y_max:
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                if x_min <= x <= x_max:
                    if tile.get('type') == 'FLOOR' and not tile.find('Object'):
                        has_marker = any(m.get('emoji') == emoji for m in tile.findall('Marker'))
                        if not has_marker:
                            unmarked.append((x, y))
    return unmarked

unmarked_coords = find_unmarked_tiles(map_xml_string, input_data['x_min'], input_data['x_max'], input_data['y_min'], input_data['y_max'], input_data['emoji'])

if input_data.get('autopress_buttons'):
    # This tool is primarily for finding, but if buttons are requested, 
    # we'd need a pathfinding integration. For now, just return the coords as a string 
    # to avoid the 'not a string' error if the system expects strings.
    print(json.dumps([str(coord) for coord in unmarked_coords]))
else:
    print(json.dumps(unmarked_coords))
