import re
import json

def parse_battle_screen(screen_text):
    player_pokemon = {}
    opponent_pokemon = {}
    lines = [line.strip() for line in screen_text.strip().split('\n')]

    try:
        # Opponent Info (Line 0)
        opp_line = lines[0]
        opp_match = re.search(r'(.+?)\s*L(\d+)', opp_line)
        if opp_match:
            opponent_pokemon['name'] = opp_match.group(1).strip()
            opponent_pokemon['level'] = int(opp_match.group(2))
        else:
            opponent_pokemon['name'] = opp_line

        # Player Info (Lines 2 and 3)
        player_line = lines[2]
        player_match = re.search(r'(.+?)\s*L(\d+)', player_line)
        if player_match:
            player_pokemon['name'] = player_match.group(1).strip()
            player_pokemon['level'] = int(player_match.group(2))
        else:
            player_pokemon['name'] = player_line

        hp_line = lines[3]
        hp_match = re.search(r'(\d+)/\s*(\d+)', hp_line)
        if hp_match:
            player_pokemon['current_hp'] = int(hp_match.group(1))
            player_pokemon['max_hp'] = int(hp_match.group(2))

    except IndexError:
        pass

    result = {
        "player_active_pokemon": player_pokemon,
        "opponent_active_pokemon": opponent_pokemon
    }
    print(json.dumps(result))

parse_battle_screen(input_data['screen_text'])

