import heapq
import json
import xml.etree.ElementTree as ET

def find_path(start_x, start_y, end_x, end_y, temp_impassable_coords, map_xml_string):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = [['' for _ in range(width)] for _ in range(height)]
    objects = {}

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id')) - 1
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id')) - 1
            grid[y][x] = tile_elem.get('type')

            obj_elem = tile_elem.find('Object')
            if obj_elem is not None:
                obj_id_name = obj_elem.get('id-name', 'Unknown Object')
                obj_name_attr = obj_elem.get('name')
                if obj_name_attr:
                    objects[(x, y)] = obj_name_attr
                else:
                    objects[(x, y)] = obj_id_name

            bg_obj_elem = tile_elem.find('Object[@name]')
            if bg_obj_elem is not None:
                objects[(x, y)] = bg_obj_elem.get('name')

    start = (start_x - 1, start_y - 1)
    end = (end_x - 1, end_y - 1)

    traversable_tiles = {'ground', 'grass', 'cuttable', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'teleport', 'gate_offscreen', 'open_gate', 'water'}

    temp_impassable = set()
    if temp_impassable_coords:
        try:
            coords_list = json.loads(temp_impassable_coords)
            for coord in coords_list:
                temp_impassable.add((int(coord['x']) - 1, int(coord['y']) - 1))
        except (json.JSONDecodeError, KeyError, TypeError):
            pass

    def is_traversable(x, y):
        if not (0 <= x < width and 0 <= y < height):
            return False

        tile_type = grid[y][x]

        if (x, y) in temp_impassable:
            return False

        if (x, y) in objects:
            obj_name = objects.get((x, y), '')
            if 'Pikachu' in obj_name:
                return True
            return False

        return tile_type in traversable_tiles

    goal_positions = []
    if is_traversable(end[0], end[1]):
        goal_positions.append(end)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = end[0] + dx, end[1] + dy
            if is_traversable(nx, ny):
                goal_positions.append((nx, ny))

    if not goal_positions:
        end_obj_name = objects.get(end, grid[end[1]][end[0]])
        error_result = {"path": [], "error": f"Destination ({end_x}, {end_y}) and all adjacent tiles are impassable. Destination contains: {end_obj_name}."}
        return json.dumps(error_result)

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (min(heuristic(start, goal) for goal in goal_positions), start))

    came_from = {}
    g_score = {start: 0}

    frontier_impassable = set()

    while open_set:
        _, current = heapq.heappop(open_set)

        if current in goal_positions:
            path = []
            while current in came_from:
                path.append({'x': current[0] + 1, 'y': current[1] + 1})
                current = came_from[current]
            path.append({'x': start[0] + 1, 'y': start[1] + 1})
            return json.dumps({"path": path[::-1]})

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            current_x, current_y = current
            neighbor = (current_x + dx, current_y + dy)

            is_ledge_jump = False
            if dy == 1 and 0 <= neighbor[1] < height and grid[neighbor[1]][neighbor[0]] == 'ledge':
                neighbor = (neighbor[0], neighbor[1] + 1)
                is_ledge_jump = True

            if not (0 <= neighbor[0] < width and 0 <= neighbor[1] < height):
                continue

            if dy == -1 and 0 <= current_y < height and grid[current_y][current_x] == 'ledge':
                continue

            if not is_traversable(neighbor[0], neighbor[1]):
                if neighbor not in g_score:
                    frontier_impassable.add(neighbor)
                continue

            current_type = grid[current_y][current_x]
            neighbor_type = grid[neighbor[1]][neighbor[0]]
            land_tiles = {'ground', 'grass'}
            water_tiles = {'water'}

            is_valid_transition = False
            if current_type == 'steps' or neighbor_type == 'steps':
                is_valid_transition = True
            elif current_type == 'elevated_ground' and neighbor_type == 'elevated_ground':
                is_valid_transition = True
            elif current_type in land_tiles and neighbor_type in land_tiles:
                is_valid_transition = True
            elif current_type in water_tiles and neighbor_type in water_tiles:
                is_valid_transition = True
            elif current_type in land_tiles and neighbor_type in water_tiles:
                is_valid_transition = True
            elif current_type in water_tiles and neighbor_type in land_tiles:
                is_valid_transition = True

            if not is_valid_transition and not is_ledge_jump:
                continue

            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + min(heuristic(neighbor, goal) for goal in goal_positions)
                if not any(item[1] == neighbor for item in open_set):
                    heapq.heappush(open_set, (f_score, neighbor))

    blocking_objects = []
    for pos in frontier_impassable:
        obj_name = objects.get(pos, grid[pos[1]][pos[0]])
        blocking_objects.append({"x": pos[0] + 1, "y": pos[1] + 1, "name": obj_name})

    is_terrain_block = False
    if blocking_objects:
        is_terrain_block = all(b['name'] == 'impassable' for b in blocking_objects)

    error_message = "No path found. The start and end points may be in disconnected map areas."
    if is_terrain_block:
        error_message = "Path blocked by impassable terrain, indicating a disconnected map segment."
    elif blocking_objects:
        blocker_details = ", ".join([f"{b['name']} at ({b['x']},{b['y']})" for b in blocking_objects[:3]])
        error_message = f"Path blocked by obstacles: {blocker_details}"
        if len(blocking_objects) > 3:
            error_message += "..."

    return json.dumps({"path": [], "error": error_message, "blocking_objects": blocking_objects})

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
temp_impassable = input_data.get('temp_impassable')

result = find_path(start_x, start_y, end_x, end_y, temp_impassable, map_xml_string)
print(result)
