import xml.etree.ElementTree as ET
import heapq
import json

def parse_coords(coord_str):
    return tuple(map(int, coord_str.strip("()").split(',')))

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(grid, start, goal, width, height):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {node: float('inf') for node in grid}
    g_score[start] = 0
    f_score = {node: float('inf') for node in grid}
    f_score[start] = heuristic(start, goal)

    while open_set:
        current = heapq.heappop(open_set)[1]

        if current == goal:
            path = []
            while current in came_from:
                previous = came_from[current]
                if current[0] == previous[0] + 1: path.append("Right")
                elif current[0] == previous[0] - 1: path.append("Left")
                elif current[1] == previous[1] + 1: path.append("Down")
                elif current[1] == previous[1] - 1: path.append("Up")
                current = previous
            return path[::-1]

        neighbors = grid.get(current, [])
        for neighbor_coords in neighbors:
            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor_coords, float('inf')):
                came_from[neighbor_coords] = current
                g_score[neighbor_coords] = tentative_g_score
                f_score[neighbor_coords] = tentative_g_score + heuristic(neighbor_coords, goal)
                if (f_score[neighbor_coords], neighbor_coords) not in open_set:
                    heapq.heappush(open_set, (f_score[neighbor_coords], neighbor_coords))
    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])

    start_node = (start_x, start_y)
    goal_node = (end_x, end_y)

    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    tiles = {}
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            coords = parse_coords(tile_elem.attrib['coordinate'])
            tile_type = tile_elem.attrib['type']
            has_object = tile_elem.attrib.get('has-object') == 'true'
            tiles[coords] = {'type': tile_type, 'is_obstacle': has_object}

    impassable_types = ['WALL', 'WATER', 'COUNTER', 'MART_SHELF', 'BOOKSHELF', 'TV', 'RADIO', 'PC', 'TOWN_MAP', 'WINDOW', 'PAINTING', 'STATUE', 'PILLAR', 'VOID', 'BUOY', 'CUT_TREE', 'HEADBUTT_TREE']

    grid = {}
    all_coords = list(tiles.keys())
    for coords in all_coords:
        if tiles[coords]['type'] in impassable_types or tiles[coords]['is_obstacle']:
            continue

        grid[coords] = []
        x, y = coords

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor_coords = (x + dx, y + dy)

            if neighbor_coords not in tiles or tiles[neighbor_coords]['type'] in impassable_types or tiles[neighbor_coords]['is_obstacle']:
                continue

            current_tile_type = tiles[coords]['type']
            neighbor_tile_type = tiles[neighbor_coords]['type']

            can_move = True
            if 'LEDGE' in neighbor_tile_type and dy != 1: can_move = False
            if 'HOP_DOWN' in current_tile_type and dy == -1: can_move = False
            if 'HOP_RIGHT' in current_tile_type and dx == -1: can_move = False
            if 'HOP_LEFT' in current_tile_type and dx == 1: can_move = False
            if 'HOP_UP' in current_tile_type and dy == 1: can_move = False
            if 'FLOOR_UP_WALL' in neighbor_tile_type and dy != -1: can_move = False

            if can_move:
                grid[coords].append(neighbor_coords)

    path = a_star_search(grid, start_node, goal_node, width, height)

    if path:
        print(json.dumps({"status": "success", "path": path}))
    else:
        print(json.dumps({"status": "failure", "path": []}))

except Exception as e:
    print(json.dumps({"status": "error", "message": str(e)}))

