import json
import heapq

world_knowledge_graph_json_string = globals().get('world_knowledge_graph_json_string')
world_knowledge = json.loads(world_knowledge_graph_json_string)
nodes = world_knowledge['nodes']
edges = world_knowledge['edges']

start_map = input_data['start_map_id']
end_map = input_data['end_map_id']

graph = {}
for node in nodes:
    graph[node['id']] = {'map_id': node['map_id'], 'name': node['name'], 'edges': []}

for edge in edges:
    if edge['source_node_id'] in graph and edge['destination_node_id'] in graph:
        graph[edge['source_node_id']]['edges'].append(edge['destination_node_id'])
        if not edge.get('is_one_way', False):
            graph[edge['destination_node_id']]['edges'].append(edge['source_node_id'])

start_node_id = None
for node_id, node_data in graph.items():
    if node_data['map_id'] == start_map:
        start_node_id = node_id
        break

if not start_node_id:
    print(f'Start node for map {start_map} not found in World Knowledge Graph.')
    exit()

queue = [(0, [start_node_id])]
visited = {start_node_id}

while queue:
    cost, path = heapq.heappop(queue)
    current_node_id = path[-1]

    if graph[current_node_id]['map_id'] == end_map:
        path_details = []
        for node_id in path:
            node_info = next((n for n in nodes if n['id'] == node_id), None)
            if node_info:
                path_details.append(f"{node_info['name']} ({node_info['map_id']})")
        print(' -> '.join(path_details))
        exit()

    for neighbor_id in graph[current_node_id]['edges']:
        if neighbor_id not in visited:
            visited.add(neighbor_id)
            new_path = list(path)
            new_path.append(neighbor_id)
            heapq.heappush(queue, (cost + 1, new_path))

print(f'No path found from map {start_map} to {end_map}.')
