import json
import xml.etree.ElementTree as ET
from collections import deque

print('--- Boulder Puzzle Solver ---')

boulder_start_x = int(input_data['boulder_x'])
boulder_start_y = int(input_data['boulder_y'])
target_x = int(input_data['target_x'])
target_y = int(input_data['target_y'])

root = ET.fromstring(map_xml_string)
width = int(root.get('width'))
height = int(root.get('height'))

grid = {}
player_start_pos = None

ignorable_sprites = ['Pikachu', 'VICTORYROAD1F_COOLTRAINER_F', 'VICTORYROAD1F_COOLTRAINER_M', 'VICTORYROAD1F_BUG_CATCHER', 'VICTORYROAD1F_BEAUTY', 'VICTORYROAD1F_BURGLAR', 'VICTORYROAD1F_BLACKBELT']

for row_elem in root.findall('Row'):
    y = int(row_elem.get('id'))
    for tile_elem in row_elem.findall('Tile'):
        x = int(tile_elem.get('id'))
        if tile_elem.find('Player') is not None:
            player_start_pos = (x, y)
        obj_node = tile_elem.find('Object')
        has_impassable_object = False
        if obj_node is not None:
            obj_id_name = obj_node.get('id-name', '')
            if obj_id_name not in ignorable_sprites:
                has_impassable_object = True
        grid[(x, y)] = {
            'type': tile_elem.get('type'),
            'has_impassable_object': has_impassable_object
        }

def get_elevation(tile_type):
    return 1 if tile_type in ['elevated_ground', 'cleared_boulder_barrier'] else 0

def can_player_reach(grid, player_start, player_goal, boulder_pos):
    impassable_types = ['impassable', 'boulder_barrier', 'cuttable', 'water']
    q = deque([player_start])
    visited = {player_start}

    if player_start == player_goal:
        return True

    if player_goal not in grid or grid[player_goal]['type'] in impassable_types or grid[player_goal]['has_impassable_object']:
        return False

    while q:
        current_pos = q.popleft()

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor_pos = (current_pos[0] + dx, current_pos[1] + dy)

            if neighbor_pos == player_goal:
                return True

            if neighbor_pos in visited or neighbor_pos not in grid or neighbor_pos == boulder_pos:
                continue

            neighbor_info = grid[neighbor_pos]
            if neighbor_info['type'] in impassable_types or neighbor_info['has_impassable_object']:
                continue

            current_type = grid[current_pos]['type']
            neighbor_type = neighbor_info['type']
            current_elevation = get_elevation(current_type)
            neighbor_elevation = get_elevation(neighbor_type)

            is_valid = False
            if current_elevation == neighbor_elevation:
                is_valid = True
            elif 'steps' in (current_type, neighbor_type):
                is_valid = True

            if is_valid:
                visited.add(neighbor_pos)
                q.append(neighbor_pos)
    return False

def solve_boulder_puzzle(grid, player_pos, boulder_pos, target_pos):
    initial_state = (player_pos, boulder_pos)
    q = deque([(initial_state, [])])
    visited_states = {initial_state}
    traversable_for_boulder = ['ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch']

    while q:
        (current_player_pos, current_boulder_pos), path = q.popleft()
        if current_boulder_pos == target_pos:
            return path
        for dx, dy, move_name in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            player_push_pos = (current_boulder_pos[0] - dx, current_boulder_pos[1] - dy)
            new_boulder_pos = (current_boulder_pos[0] + dx, current_boulder_pos[1] + dy)
            if new_boulder_pos not in grid:
                continue
            boulder_dest_tile = grid[new_boulder_pos]
            if boulder_dest_tile['type'] not in traversable_for_boulder or boulder_dest_tile['has_impassable_object']:
                continue
            if not can_player_reach(grid, current_player_pos, player_push_pos, current_boulder_pos):
                continue
            new_state = (player_push_pos, new_boulder_pos)
            if new_state not in visited_states:
                visited_states.add(new_state)
                new_path = path + [{'player_move_to': player_push_pos, 'boulder_push_to': new_boulder_pos}]
                q.append((new_state, new_path))
    return None

solution = solve_boulder_puzzle(grid, player_start_pos, (boulder_start_x, boulder_start_y), (target_x, target_y))

if solution:
    print(json.dumps(solution))
else:
    print(json.dumps({'error': 'No solution found.'}))

