import heapq
import json
import xml.etree.ElementTree as ET

class AStar:
    def __init__(self, map_xml_string):
        self.root = ET.fromstring(map_xml_string)
        self.width = int(self.root.get('width'))
        self.height = int(self.root.get('height'))
        self.grid = [[None for _ in range(self.width + 1)] for _ in range(self.height + 1)]
        self.parse_map()

    def parse_map(self):
        ignorable_sprites = ['Pikachu', 'VICTORYROAD1F_COOLTRAINER_F', 'VICTORYROAD1F_COOLTRAINER_M', 'VICTORYROAD1F_BUG_CATCHER', 'VICTORYROAD1F_BEAUTY', 'VICTORYROAD1F_BURGLAR', 'VICTORYROAD1F_BLACKBELT']
        for row_elem in self.root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                tile_type = tile_elem.get('type')

                has_obstacle = False
                object_elem = tile_elem.find('Object')
                if object_elem is not None:
                    obj_id_name = object_elem.get('id-name', '')
                    if obj_id_name not in ignorable_sprites:
                        has_obstacle = True

                if tile_elem.find('Boulder') is not None:
                    has_obstacle = True

                self.grid[y][x] = {'type': tile_type, 'has_object': has_obstacle}

    def is_traversable(self, x, y, movement_mode, extra_impassable_coords):
        if not (1 <= x <= self.width and 1 <= y <= self.height):
            return False
        tile = self.grid[y][x]
        if tile is None or (x, y) in extra_impassable_coords or tile['has_object']:
            return False
        traversable_tiles = ['ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down']
        if movement_mode == 'surfing':
            traversable_tiles.append('water')
        return tile['type'] in traversable_tiles

    def get_elevation(self, x, y):
        tile_type = self.grid[y][x]['type']
        if tile_type in ['elevated_ground', 'cleared_boulder_barrier']:
            return 1
        return 0

    def get_neighbors(self, node, end_node, movement_mode, extra_impassable_coords):
        x, y = node
        neighbors = []
        current_elev = self.get_elevation(x, y)
        current_type = self.grid[y][x]['type']

        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy

            if not (1 <= nx <= self.width and 1 <= ny <= self.height):
                continue

            neighbor_tile = self.grid[ny][nx]
            neighbor_type = neighbor_tile['type']
            neighbor_elev = self.get_elevation(nx, ny)

            # Universal impassable check. Must come first.
            is_impassable = not self.is_traversable(nx, ny, movement_mode, extra_impassable_coords)
            if is_impassable and neighbor_type != 'ledge' and (nx, ny) != end_node:
                continue

            # Case 1: Ledge traversal (one-way down)
            if neighbor_type == 'ledge':
                if move == 'Down' and current_elev == 1:
                    landing_y = ny + 1
                    if 1 <= landing_y <= self.height and self.is_traversable(nx, landing_y, movement_mode, extra_impassable_coords):
                        neighbors.append(((nx, landing_y), move))
                continue

            # Case 2: Same elevation traversal
            if current_elev == neighbor_elev:
                if self.is_traversable(nx, ny, movement_mode, extra_impassable_coords):
                    neighbors.append(((nx, ny), move))
                continue

            # Case 3: Elevation change via steps
            if current_elev != neighbor_elev:
                if (current_type == 'steps' or neighbor_type == 'steps') and self.is_traversable(nx, ny, movement_mode, extra_impassable_coords):
                    neighbors.append(((nx, ny), move))
                continue

        return neighbors

    def heuristic(self, a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def find_path(self, start, end, movement_mode, extra_impassable_coords):
        open_set = [(0, start)]
        came_from = {}
        g_score = {start: 0}
        f_score = {start: self.heuristic(start, end)}

        while open_set:
            _, current = heapq.heappop(open_set)

            if current == end:
                path = []
                while current in came_from:
                    path.append(came_from[current][1])
                    current = came_from[current][0]
                return path[::-1]

            for neighbor, move in self.get_neighbors(current, end, movement_mode, extra_impassable_coords):
                tentative_g_score = g_score[current] + 1
                if tentative_g_score < g_score.get(neighbor, float('inf')):
                    came_from[neighbor] = (current, move)
                    g_score[neighbor] = tentative_g_score
                    f_score[neighbor] = g_score[neighbor] + self.heuristic(neighbor, end)
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

        return None

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
movement_mode = input_data.get('movement_mode', 'walking')
extra_impassable_str = input_data.get('extra_impassable', '[]')
extra_impassable = [tuple(item) for item in json.loads(extra_impassable_str)]

astar = AStar(map_xml_string)
path = astar.find_path((start_x, start_y), (end_x, end_y), movement_mode, extra_impassable)

if path:
    print(f"Path found: {', '.join(path)}")
else:
    print("No path found")

