import collections
import xml.etree.ElementTree as ET
import json
import sys

def solve():
    global map_xml_string
    if 'map_xml_string' not in globals() or not map_xml_string:
        print(json.dumps([]))
        return

    input_data = {}
    try:
        raw_input = sys.stdin.read()
        if raw_input:
            input_data = json.loads(raw_input)
    except:
        pass

    root = ET.fromstring(map_xml_string)
    if 'end_x' not in input_data or 'end_y' not in input_data:
        print(json.dumps([]))
        return

    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    can_cut = input_data.get('can_cut', True)
    can_surf = input_data.get('can_surf', True)

    grid = {}
    player_pos = None

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            has_player = tile.find('Player') is not None
            has_object = tile.find('Object') is not None

            if has_player:
                player_pos = (x, y)

            grid[(x, y)] = {
                'type': t_type,
                'has_object': has_object
            }

    if player_pos is None:
        print(json.dumps([]))
        return

    def get_buttons(path):
        buttons = []
        for i in range(len(path) - 1):
            curr = path[i]
            nxt = path[i+1]
            if nxt[0] > curr[0]: buttons.append("Right")
            elif nxt[0] < curr[0]: buttons.append("Left")
            elif nxt[1] > curr[1]: buttons.append("Down")
            elif nxt[1] < curr[1]: buttons.append("Up")
        return buttons

    queue = collections.deque([(player_pos, [])])
    visited = {player_pos}

    impassable_types = {'WALL', 'BUOY', 'COUNTER', 'HEADBUTT_TREE'}

    while queue:
        (curr_x, curr_y), path = queue.popleft()

        if curr_x == end_x and curr_y == end_y:
            print(json.dumps(get_buttons(path + [(curr_x, curr_y)])))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if (nx, ny) in grid and (nx, ny) not in visited:
                tile = grid[(nx, ny)]

                # Collision checks
                is_blocked = False
                if tile['type'] in impassable_types: is_blocked = True
                if tile['type'] == 'CUT_TREE' and not can_cut: is_blocked = True
                if tile['type'] == 'WATER' and not can_surf: is_blocked = True
                # If target is not a known object, treat all objects as walls
                if tile['has_object'] and (nx != end_x or ny != end_y): is_blocked = True

                if not is_blocked:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [(curr_x, curr_y)]))

    print(json.dumps([]))

if __name__ == "__main__":
    solve()

