import collections
import xml.etree.ElementTree as ET
import json
import sys

def solve():
    global map_xml_string
    if 'map_xml_string' not in globals() or not map_xml_string:
        print(json.dumps([]))
        return

    try:
        input_data = json.load(sys.stdin)
    except:
        input_data = {}

    root = ET.fromstring(map_xml_string)
    target_x = int(input_data.get('target_x', 0))
    target_y = int(input_data.get('target_y', 0))

    grid = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            if tile.find('Player') is not None: player_pos = (x, y)
            grid[(x, y)] = {
                'type': tile.get('type'),
                'has_object': tile.find('Object') is not None
            }

    if player_pos is None:
        print(json.dumps([]))
        return

    def get_buttons(path):
        buttons = []
        for i in range(len(path) - 1):
            curr = path[i]
            nxt = path[i+1]
            if nxt[0] > curr[0]: buttons.append("Right")
            elif nxt[0] < curr[0]: buttons.append("Left")
            elif nxt[1] > curr[1]: buttons.append("Down")
            elif nxt[1] < curr[1]: buttons.append("Up")
        return buttons

    queue = collections.deque([(player_pos, [])])
    visited = {player_pos}
    impassable = {'WALL', 'BUOY', 'COUNTER', 'HEADBUTT_TREE'}

    best_path = None
    best_adj = None

    while queue:
        (curr_x, curr_y), path = queue.popleft()

        if abs(curr_x - target_x) + abs(curr_y - target_y) == 1:
            best_path = path + [(curr_x, curr_y)]
            best_adj = (curr_x, curr_y)
            break

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if (nx, ny) in grid and (nx, ny) not in visited:
                tile = grid[(nx, ny)]
                if tile['type'] in impassable or tile['has_object']: continue
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [(curr_x, curr_y)]))

    if best_path:
        buttons = get_buttons(best_path)
        if target_x > best_adj[0]: buttons.append("Right")
        elif target_x < best_adj[0]: buttons.append("Left")
        elif target_y > best_adj[1]: buttons.append("Down")
        elif target_y < best_adj[1]: buttons.append("Up")
        buttons.append("A")
        print(json.dumps(buttons))
    else:
        print(json.dumps([]))

if __name__ == "__main__":
    solve()
