import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path(xml_string, start_x, start_y, target_x, target_y):
    root = ET.fromstring(xml_string)
    grid = {}
    objects = set()
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            grid[(x, y)] = tile.get('type')
            if tile.find('Object') is not None:
                objects.add((x, y))

    def can_move(x, y, nx, ny, surfing):
        t_type = grid.get((nx, ny))
        if t_type is None: return False
        if (nx, ny) in objects: return False

        # Collision rules
        if t_type in ["WALL", "BUOY"]: return False

        # Surfing constraints
        if surfing:
            if t_type != "WATER": return False
        else:
            if t_type == "WATER": return False

        # Directional mechanics
        dx, dy = nx - x, ny - y
        curr_type = grid.get((x, y))

        # Ledge: One-way Down (South)
        if t_type == "LEDGE_HOP_DOWN":
            if dy != 1: return False # Must enter by jumping down
        if curr_type == "LEDGE_HOP_DOWN":
            if dy != 1: return False # Must exit by jumping down

        # FLOOR_UP_WALL: Impassable from North
        if dy == 1: # Moving South
            if t_type == "FLOOR_UP_WALL": return False # Cannot enter from North
        if dy == -1: # Moving North
            if curr_type == "FLOOR_UP_WALL": return False # Cannot exit to North

        return True

    queue = deque([((start_x, start_y), [], False)])
    visited = {((start_x, start_y), False)}

    while queue:
        (x, y), path, surfing = queue.popleft()
        if (x, y) == (target_x, target_y):
            return path + [(x, y)]

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if (nx, ny, surfing) not in visited:
                if can_move(x, y, nx, ny, surfing):
                    visited.add((nx, ny, surfing))
                    queue.append(((nx, ny), path + [(x, y)], surfing))

        if not surfing:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = x + dx, y + dy
                if grid.get((nx, ny)) == "WATER" and (nx, ny, True) not in visited:
                    visited.add((nx, ny, True))
                    queue.append(((nx, ny), path + [(x, y)], True))
        else:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = x + dx, y + dy
                if grid.get((nx, ny)) in ["FLOOR", "FLOOR_UP_WALL", "DOOR", "LEDGE_HOP_DOWN"] and (nx, ny, False) not in visited:
                    if can_move(x, y, nx, ny, False):
                        visited.add((nx, ny, False))
                        queue.append(((nx, ny), path + [(x, y)], False))
    return None

