import json
import xml.etree.ElementTree as ET
import collections

try:
    player_tile = next(tile for row in ET.fromstring(map_xml_string).findall('Row') for tile in row.findall('Tile') if tile.find('Player') is not None)
    player_pos_str = player_tile.get('coordinate')
    start_x, start_y = map(int, player_pos_str.strip('()').split(', '))

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    traversable_types = ['FLOOR', 'DOOR', 'WARP_CARPET_DOWN']
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[(x, y)] = {
                'type': tile_elem.get('type'),
                'seen': tile_elem.get('seen') == 'true',
                'has_object': tile_elem.find('Object') is not None
            }

    queue = collections.deque([(start_x, start_y)])
    reachable = set([(start_x, start_y)])
    while queue:
        x, y = queue.popleft()
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid and (nx, ny) not in reachable:
                tile_info = grid.get((nx, ny))
                if tile_info and tile_info['type'] in traversable_types and not tile_info['has_object']:
                    reachable.add((nx, ny))
                    queue.append((nx, ny))

    reachable_unseen = []
    for x_coord in range(1, width + 1):
        for y_coord in range(1, height + 1):
            if (x_coord, y_coord) in grid and not grid[(x_coord, y_coord)]['seen']:
                is_adjacent_to_reachable = False
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    nx, ny = x_coord + dx, y_coord + dy
                    if (nx, ny) in reachable:
                        is_adjacent_to_reachable = True
                        break
                if is_adjacent_to_reachable:
                    reachable_unseen.append({'x': x_coord, 'y': y_coord})

    print(json.dumps(reachable_unseen))

except Exception as e:
    print(json.dumps({"error": str(e)}))
