from collections import deque
import json

def run(input_data):
    end_x = int(input_data.get('end_x'))
    end_y = int(input_data.get('end_y'))
    can_surf = input_data.get('can_surf', False)
    if isinstance(can_surf, str):
        can_surf = can_surf.lower() == 'true'

    if not map_xml_string or "<Map" not in map_xml_string:
        return ["Error: Mental Map unavailable"]

    root = ET.fromstring(map_xml_string)

    start_x = input_data.get('start_x')
    start_y = input_data.get('start_y')

    if start_x is None or start_y is None:
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    start_x, start_y = int(tile.attrib['id']), y
                    break
            if start_x is not None: break

    if start_x is None: return ["Error: Player position not found"]

    grid = {}
    impassable_types = {'WALL', 'HEADBUTT_TREE', 'unseen'}
    if not can_surf:
        impassable_types.add('WATER')

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            grid[(x, y)] = t_type
            if tile.find('Object') is not None and (x != end_x or y != end_y):
                grid[(x, y)] = 'WALL'

    queue = deque([(start_x, start_y, [])])
    visited = {(start_x, start_y)}

    while queue:
        cx, cy, path = queue.popleft()
        if cx == end_x and cy == end_y:
            return path

        for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in grid:
                t_type = grid[(nx, ny)]

                # Relational movement logic (one-way tiles)
                if dy == 1: # Moving South
                    if t_type == 'LEDGE_HOP_UP' or t_type == 'FLOOR_UP_WALL': continue
                    if t_type == 'LEDGE_HOP_DOWN':
                        ny += 1
                        if (nx, ny) in grid: t_type = grid[(nx, ny)]
                        else: continue
                elif dy == -1: # Moving North
                    if t_type == 'LEDGE_HOP_DOWN': continue
                    if t_type == 'LEDGE_HOP_UP':
                        ny -= 1
                        if (nx, ny) in grid: t_type = grid[(nx, ny)]
                        else: continue
                elif dx == 1: # Moving East
                    if t_type == 'LEDGE_HOP_LEFT': continue
                    if t_type == 'LEDGE_HOP_RIGHT':
                        nx += 1
                        if (nx, ny) in grid: t_type = grid[(nx, ny)]
                        else: continue
                elif dx == -1: # Moving West
                    if t_type == 'LEDGE_HOP_RIGHT': continue
                    if t_type == 'LEDGE_HOP_LEFT':
                        nx -= 1
                        if (nx, ny) in grid: t_type = grid[(nx, ny)]
                        else: continue

                if t_type not in impassable_types or (nx == end_x and ny == end_y):
                    if (nx, ny) not in visited:
                        visited.add((nx, ny))
                        queue.append((nx, ny, path + [btn]))

    return ["Error: No path found"]

