from collections import deque
import json

def run(input_data):
    can_surf = input_data.get('can_surf', False)
    if isinstance(can_surf, str):
        can_surf = can_surf.lower() == 'true'

    if not map_xml_string or "<Map" not in map_xml_string:
        return {"error": "Mental Map unavailable."}

    root = ET.fromstring(map_xml_string)

    start_x, start_y = None, None
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            if tile.find('Player') is not None:
                start_x, start_y = int(tile.attrib['id']), y
                break
        if start_x is not None: break

    if start_x is None: return {"reachable_unseen": []}

    grid = {}
    impassable_types = {'WALL', 'HEADBUTT_TREE', 'unseen'}
    if not can_surf:
        impassable_types.add('WATER')

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            grid[(x, y)] = t_type
            if tile.find('Object') is not None: grid[(x, y)] = 'WALL'

    queue = deque([(start_x, start_y)])
    visited = {(start_x, start_y)}
    reachable_tiles = {(start_x, start_y)}

    while queue:
        curr_x, curr_y = queue.popleft()
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if (nx, ny) in grid:
                t_type = grid[(nx, ny)]

                # Ledge/One-way logic
                if dy == 1: # Moving South
                    if t_type == 'LEDGE_HOP_UP' or t_type == 'FLOOR_UP_WALL': continue
                elif dy == -1: # Moving North
                    if t_type == 'LEDGE_HOP_DOWN': continue
                elif dx == 1: # Moving East
                    if t_type == 'LEDGE_HOP_LEFT': continue
                elif dx == -1: # Moving West
                    if t_type == 'LEDGE_HOP_RIGHT': continue

                # Hopping
                if dy == 1 and t_type == 'LEDGE_HOP_DOWN':
                    ny += 1
                    if (nx, ny) in grid: t_type = grid[(nx, ny)]
                    else: continue
                elif dy == -1 and t_type == 'LEDGE_HOP_UP':
                    ny -= 1
                    if (nx, ny) in grid: t_type = grid[(nx, ny)]
                    else: continue
                elif dx == 1 and t_type == 'LEDGE_HOP_RIGHT':
                    nx += 1
                    if (nx, ny) in grid: t_type = grid[(nx, ny)]
                    else: continue
                elif dx == -1 and t_type == 'LEDGE_HOP_LEFT':
                    nx -= 1
                    if (nx, ny) in grid: t_type = grid[(nx, ny)]
                    else: continue

                if t_type not in impassable_types:
                    if (nx, ny) not in visited:
                        visited.add((nx, ny))
                        reachable_tiles.add((nx, ny))
                        queue.append((nx, ny))

    reachable_unseen = set()
    for rx, ry in reachable_tiles:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            ux, uy = rx + dx, ry + dy
            if (ux, uy) in grid and grid[(ux, uy)] == 'unseen':
                reachable_unseen.add((ux, uy))

    return {"reachable_unseen": sorted(list(reachable_unseen))}

print(json.dumps(run(input_data)))

