import xml.etree.ElementTree as ET
import json

def run(input_data):
    target_type = input_data.get('tile_type')
    if not target_type:
        return {"error": "Missing tile_type"}

    if not map_xml_string or "<Map" not in map_xml_string:
        return {"error": "Mental Map unavailable."}

    root = ET.fromstring(map_xml_string)
    found_tiles = []
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            if tile.attrib.get('type') == target_type:
                found_tiles.append([int(tile.attrib['id']), y])
    return {"tiles": found_tiles}

print(json.dumps(run(input_data)))
