import json
import collections
import xml.etree.ElementTree as ET
import sys

try:
    start_x_str = input_data['start_x']
    start_y_str = input_data['start_y']
    end_x_str = input_data['end_x']
    end_y_str = input_data['end_y']
    is_surfing_str = input_data.get('is_surfing')

    is_surfing = is_surfing_str.lower() == 'true' if is_surfing_str is not None else False
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}

    key_obstacles = {'cuttable': [], 'boulder': []}

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_object = tile_elem.get('has-object') == 'true'
            object_id, object_name = None, None
            if has_object:
                obj = tile_elem.find('Object')
                if obj is not None:
                    object_id = obj.get('id')
                    object_name = obj.get('id-name')
                    if 'boulder' in (object_name or '').lower():
                        key_obstacles['boulder'].append({'x': x, 'y': y, 'name': object_name})

            tiles[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_id': object_id, 'object_name': object_name}
            if tile_type == 'cuttable':
                key_obstacles['cuttable'].append({'x': x, 'y': y})


    start = (int(start_x_str), int(start_y_str))
    end_goal = (int(end_x_str), int(end_y_str))

    traversable_base_types = {'ground', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'ladder_up', 'ladder_down', 'grass', 'gate_offscreen', 'open_gate'}
    if is_surfing:
        traversable_base_types.add('water')

    def get_traversability_reason(x, y, is_destination=False):
        if not (1 <= x <= map_width and 1 <= y <= map_height): return 'out_of_bounds'
        tile_info = tiles.get((x, y))
        if not tile_info: return 'no_tile_info'
        if tile_info['type'] == 'unknown': return 'unseen_tile'
        if tile_info['type'] in ['impassable', 'boulder_barrier', 'closed_gate']: return f"impassable_type_{tile_info['type']}"
        if tile_info['type'] == 'cuttable': return 'cuttable_tree'
        if tile_info['type'] == 'teleport' and not is_destination: return 'teleport_avoided'
        if not is_surfing and tile_info['type'] == 'water': return 'water_requires_surf'

        if tile_info['has_object'] and tile_info['object_id'] != '15':
            if 'HOLE' in (tile_info.get('object_name', '') or '').upper():
                return 'traversable'
            return f"object_blocking_{tile_info.get('object_name', 'Unknown')}"

        if tile_info['type'] not in traversable_base_types and tile_info['type'] != 'teleport' and tile_info['type'] != 'ledge': return f"non_traversable_type_{tile_info['type']}"
        return 'traversable'

    goal_set = set()
    end_goal_reason = get_traversability_reason(end_goal[0], end_goal[1], is_destination=True)
    if end_goal_reason == 'traversable' or end_goal_reason.startswith('non_traversable_type_ledge'):
        goal_set.add(end_goal)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (end_goal[0] + dx, end_goal[1] + dy)
            neighbor_tile_info = tiles.get(neighbor)
            if not neighbor_tile_info: continue

            if neighbor_tile_info['type'] == 'ledge' and end_goal[1] == neighbor[1] + 1:
                if get_traversability_reason(neighbor[0], neighbor[1], is_destination=True) == 'traversable':
                     goal_set.add(neighbor)
                continue

            if get_traversability_reason(neighbor[0], neighbor[1], is_destination=True) == 'traversable':
                goal_set.add(neighbor)

    if not goal_set:
        reason = get_traversability_reason(end_goal[0], end_goal[1])
        result = {'path_found': False, 'error': f'Destination and all adjacent tiles are not traversable. Destination reason: {reason}'}
        print(json.dumps(result))
        sys.exit()

    queue = collections.deque([(start, [start])])
    visited = {start}
    frontier_obstacles = {}

    while queue:
        (current_x, current_y), path = queue.popleft()
        current_pos = (current_x, current_y)
        current_tile_info = tiles.get(current_pos)

        if current_pos in goal_set:
            result = {'path_found': True, 'path': path}
            print(json.dumps(result))
            sys.exit()

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_pos = (current_x + dx, current_y + dy)
            if next_pos in visited: continue

            next_tile_info = tiles.get(next_pos)
            if not next_tile_info: continue

            if current_tile_info['type'] in ['ground', 'elevated_ground'] and next_tile_info['type'] in ['ground', 'elevated_ground']:
                if current_tile_info['type'] != next_tile_info['type'] and current_tile_info['type'] != 'steps' and next_tile_info['type'] != 'steps':
                    frontier_obstacles[next_pos] = 'elevation_mismatch'
                    continue

            if next_tile_info['type'] == 'ledge':
                if dy == 1:
                    hop_dest = (current_x, current_y + 2)
                    if hop_dest in visited: continue
                    visited.add(hop_dest)
                    queue.append((hop_dest, path + [hop_dest]))
                else:
                    frontier_obstacles[next_pos] = 'ledge_hop_up_or_sideways'
                continue

            traversability_reason = get_traversability_reason(next_pos[0], next_pos[1])
            if traversability_reason == 'traversable':
                visited.add(next_pos)
                queue.append((next_pos, path + [next_pos]))
            else:
                frontier_obstacles[next_pos] = traversability_reason

    unreachable_key_obstacles = {'cuttable': [], 'boulder': []}
    for obs_type, obs_list in key_obstacles.items():
        for obs in obs_list:
            pos = (obs['x'], obs['y'])
            is_reachable = False
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                if (pos[0] + dx, pos[1] + dy) in visited:
                    is_reachable = True
                    break
            if not is_reachable:
                unreachable_key_obstacles[obs_type].append(obs)

    report = {
        'path_found': False,
        'error': 'Path blocked. Analysis follows.',
        'frontier_analysis': [{'coords': f'({k[0]},{k[1]})', 'reason': v} for k, v in frontier_obstacles.items()],
        'unreachable_key_obstacles': unreachable_key_obstacles
    }
    print(json.dumps(report, indent=2))

except Exception as e:
    import traceback
    error_info = traceback.format_exc()
    print(json.dumps({'path_found': False, 'error': f'Script failed: {e}', 'traceback': error_info}))

