import json
import sys

try:
    player_party = json.loads(input_data['player_party_json'])
    enemy_pokemon = json.loads(input_data['enemy_pokemon_json'])
    active_pokemon_name = input_data['active_pokemon_name']
    moves_pp = json.loads(input_data['player_party_moves_pp_json'])

    active_pokemon = next((p for p in player_party if p['species'] == active_pokemon_name), None)

    if not active_pokemon or not enemy_pokemon.get('is_wild', False):
        print(json.dumps(['A']))
        sys.exit()

    level_difference = active_pokemon['level'] - enemy_pokemon['level']
    if level_difference < 20:
        print(json.dumps(['A']))
        sys.exit()

    active_pokemon_moves = next((m for m in moves_pp if m['species'] == active_pokemon_name), None)
    if not active_pokemon_moves or not active_pokemon_moves['moves']:
        print(json.dumps(['A']))
        sys.exit()

    first_move = active_pokemon_moves['moves'][0]
    if first_move['pp'] == 0:
        print(json.dumps(['A']))
        sys.exit()

    MOVE_TYPES = {'THUNDERBOLT': 'Electric', 'FLASH': 'Normal', 'LIGHT SCREEN': 'Psychic', 'THUNDER WAVE': 'Electric'}
    POKEMON_TYPES = {'GASTLY': ['Ghost', 'Poison'], 'CUBONE': ['Ground', ''], 'DIGLETT': ['Ground', '']}
    IMMUNITIES = {'Electric': ['Ground']}

    move_type = MOVE_TYPES.get(first_move['name'])
    enemy_types = POKEMON_TYPES.get(enemy_pokemon['species'])

    if move_type and enemy_types:
        if any(enemy_type in IMMUNITIES.get(move_type, []) for enemy_type in enemy_types if enemy_type):
            print(json.dumps(['A'])) 
            sys.exit()

    print(json.dumps(['A', 'A']))

except Exception:
    print(json.dumps(['A']))
