import json
import collections
import xml.etree.ElementTree as ET
import sys

def find_path_with_validation():
    try:
        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        end_x = int(input_data['end_x'])
        end_y = int(input_data['end_y'])
        movement_state = input_data['movement_state']
        temp_passable_coords_str = input_data.get('temp_passable_coords')
        temp_passable_coords = set()
        if temp_passable_coords_str:
            try:
                coords_list = json.loads(temp_passable_coords_str)
                temp_passable_coords = {tuple(c) for c in coords_list}
            except (json.JSONDecodeError, TypeError):
                pass

        root = ET.fromstring(map_xml_string)
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))
        tiles = {}
        end = (end_x, end_y)
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile_elem in row.findall('Tile'):
                x = int(tile_elem.get('id'))
                obj = tile_elem.find('Object')
                warp = tile_elem.find('Warp')
                tiles[(x, y)] = {
                    'type': tile_elem.get('type'),
                    'has_object': tile_elem.get('has-object') == 'true',
                    'is_warp': warp is not None,
                    'object_id_name': obj.get('id-name') if obj is not None else None,
                    'object_name': obj.get('name') if obj is not None else None
                }

        start = (start_x, start_y)

        base_traversable_tile_types = {
            'ground', 'grass', 'elevated_ground', 'steps', 
            'teleport', 'hole', 'cleared_boulder_barrier',
            'open_gate', 'gate_offscreen', 'spinner_stop' 
        }

        if movement_state == 'surfing':
            base_traversable_tile_types.add('water')

        def get_tile_info(pos):
            return tiles.get(pos)

        def get_blockage_reason(pos):
            tile_info = get_tile_info(pos)
            if not tile_info:
                return f'Tile at {pos} does not exist in map data.'
            if tile_info.get('is_warp') and pos != end:
                return 'Blocked by a warp tile that is not the destination.'
            if tile_info.get('has_object'):
                obj_name = tile_info.get('object_name') or tile_info.get('object_id_name')
                if (obj_name or '').upper() != 'PIKACHU':
                    return f'Blocked by object: {obj_name}'
            tile_type = tile_info['type']
            if tile_type == 'impassable':
                return 'Blocked by impassable wall.'
            if tile_type == 'cuttable':
                return 'Blocked by a cuttable tree (requires Cut).'
            if tile_type == 'water' and movement_state != 'surfing':
                return 'Blocked by water (requires Surf).'
            if tile_type == 'closed_gate':
                return 'Blocked by a closed gate.'
            if tile_type == 'boulder_barrier':
                return 'Blocked by a boulder-activated barrier.'
            return f'Blocked by unhandled tile type: {tile_type}.'

        def is_valid_move(pos, from_pos=None):
            if not (1 <= pos[0] <= map_width and 1 <= pos[1] <= map_height):
                return False

            tile_info = get_tile_info(pos)
            if not tile_info:
                return False

            if tile_info.get('is_warp') and pos != end:
                return False

            if tile_info.get('type') == 'impassable':
                return False

            if pos in temp_passable_coords:
                return True

            if tile_info.get('has_object'):
                is_pikachu = (tile_info.get('object_id_name') or '').upper() == 'PIKACHU'
                if not is_pikachu:
                    return False

            tile_type = tile_info['type']
            if tile_type in {'closed_gate'}:
                return False

            if 'type' not in tile_info or tile_info['type'] is None:
                return False

            if tile_type in {'ladder_up', 'ladder_down'} and pos != end:
                return False

            traversable_types = base_traversable_tile_types.copy()
            if pos == end and tile_type in {'ladder_up', 'ladder_down'}:
                traversable_types.add(tile_type)

            if tile_type not in traversable_types:
                return False

            if from_pos:
                current_tile_info = get_tile_info(from_pos)
                current_type = current_tile_info.get('type')
                next_type = tile_info.get('type')

                is_elevation_change = (current_type == 'ground' and next_type == 'elevated_ground') or \
                                      (current_type == 'elevated_ground' and next_type == 'ground')

                is_via_steps = (current_type == 'steps' or next_type == 'steps')

                if is_elevation_change and not is_via_steps:
                    return False

            return True

        goal_tiles = []
        if is_valid_move(end):
            goal_tiles.append(end)
        else:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adj_pos = (end_x + dx, end_y + dy)
                if is_valid_move(adj_pos):
                    goal_tiles.append(adj_pos)

        if not goal_tiles:
            reason = f"Destination ({end_x},{end_y}) and all adjacent tiles are impassable. Destination blockage: {get_blockage_reason(end)}"
            print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))
            return

        queue = collections.deque([start])
        visited = {start}
        came_from = {start: None}
        final_goal = None

        while queue:
            current_pos = queue.popleft()

            if current_pos in goal_tiles:
                final_goal = current_pos
                break

            (cx, cy) = current_pos
            for dx, dy in [(0, 1), (1, 0), (-1, 0), (0, -1)]:
                next_pos = (cx + dx, cy + dy)

                if next_pos in visited:
                    continue

                tile_data = get_tile_info(next_pos)
                if not tile_data: continue

                if tile_data.get('type') == 'ledge':
                    if dy == 1 and dx == 0: # Hopping down
                        final_pos = (cx, cy + 2)
                        if final_pos not in visited and is_valid_move(final_pos, from_pos=current_pos):
                            visited.add(next_pos)
                            visited.add(final_pos)
                            came_from[final_pos] = current_pos
                            queue.append(final_pos)
                        continue

                if is_valid_move(next_pos, from_pos=current_pos):
                    visited.add(next_pos)
                    came_from[next_pos] = current_pos
                    queue.append(next_pos)

        if final_goal:
            path = []
            curr = final_goal
            while curr is not None:
                path.append(curr)
                curr = came_from.get(curr)
            path.reverse()

            final_path_plan = [{'x': p[0], 'y': p[1]} for p in path]
            print(json.dumps({'success': True, 'reason': 'Path found.', 'path_plan': final_path_plan}))
        else:
            boundary_tiles = set()
            for pos in visited:
                (cx, cy) = pos
                for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                    next_pos = (cx + dx, cy + dy)
                    if next_pos not in visited and get_tile_info(next_pos):
                        boundary_tiles.add(next_pos)

            closest_obstacle = None
            min_dist = float('inf')

            for pos in boundary_tiles:
                if not is_valid_move(pos):
                    dist = abs(pos[0] - start_x) + abs(pos[1] - start_y)
                    if dist < min_dist:
                        min_dist = dist
                        closest_obstacle = pos

            if closest_obstacle:
                reason = get_blockage_reason(closest_obstacle)
                final_reason = f'Pathfinding failed. Closest obstacle at {closest_obstacle}: {reason}'
            else:
                final_reason = f"Destination ({end_x},{end_y}) is in an unreachable partition of the map with no identifiable obstacles on the boundary."

            print(json.dumps({'success': False, 'reason': final_reason, 'path_plan': []}))

    except Exception as e:
        import traceback
        exc_info = sys.exc_info()
        reason = f"An error occurred: {str(e)}\n{traceback.format_exc()}"
        print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))

find_path_with_validation()

