import json
import xml.etree.ElementTree as ET

root = ET.fromstring(map_xml_string)
npcs = []
background_objects = []
items = []

for row in root.findall('Row'):
    y_coord = int(row.get('id'))
    for tile in row.findall('Tile'):
        x_coord = int(tile.get('id'))
        obj = tile.find('Object')
        if obj is not None:
            obj_info = {
                'name': obj.get('name') or obj.get('id-name'),
                'x': x_coord,
                'y': y_coord
            }
            if obj.get('id-name'):
                if 'ITEM' in (obj.get('id-name') or '').upper():
                    items.append(obj_info)
                else:
                    npcs.append(obj_info)
            else:
                background_objects.append(obj_info)

output = {
    'npcs': npcs,
    'background_objects': background_objects,
    'items': items
}

print(json.dumps(output))
