import xml.etree.ElementTree as ET
import json
import collections

def find_path(map_xml, start, end, can_surf):
    root = ET.fromstring(map_xml)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            # Basic collision logic
            is_walkable = t_type in ["FLOOR", "TALL_GRASS", "DOOR", "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT", "CAVE", "STAIRCASE", "LADDER"]
            if can_surf and t_type == "WATER":
                is_walkable = True

            # Objects act as walls
            if tile.find('Object') is not None:
                is_walkable = False

            grid[(x, y)] = is_walkable

    queue = collections.deque([(start, [start])])
    visited = {start}

    while queue:
        (x, y), path = queue.popleft()
        if (x, y) == end:
            return path

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                if grid.get((nx, ny), False) or (nx, ny) == end:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [(nx, ny)]))
    return None

