import xml.etree.ElementTree as ET
import heapq
import json

def solve():
    try:
        tree = ET.fromstring(map_xml_string)
    except Exception:
        print(json.dumps([]))
        return

    width = int(tree.get('width'))
    height = int(tree.get('height'))
    start = (int(input_data['start_x']), int(input_data['start_y']))
    end = (int(input_data['end_x']), int(input_data['end_y']))

    grid = {}
    for row in tree.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            has_obj = tile.get('has-object') == 'true'
            grid[(x, y)] = {'type': t_type, 'seen': seen, 'has_obj': has_obj}

    def is_walkable(pos, from_pos=None):
        if pos not in grid: return False
        tile = grid[pos]
        if tile['has_obj'] and pos != start and pos != end: return False
        if not tile['seen']: return True

        walkable_types = ["FLOOR", "TALL_GRASS", "LONG_GRASS", "LADDER", "PIER", "grass", "DOOR", "STAIRCASE", "CAVE", "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT"]
        if tile['type'] in walkable_types or "WARP_CARPET" in tile['type']:
            return True
        if tile['type'] == "LEDGE_HOP_DOWN":
            if from_pos and from_pos[0] == pos[0] and from_pos[1] == pos[1] - 1:
                return True
        return False

    queue = [(0, start, [])]
    visited = set()

    while queue:
        (cost, current, path) = heapq.heappop(queue)
        if current in visited: continue
        visited.add(current)
        if current == end:
            print(json.dumps(path))
            return

        x, y = current
        for dx, dy, btn in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
            neighbor = (x + dx, y + dy)
            if is_walkable(neighbor, current):
                new_path = path + [btn]
                priority = len(new_path) + abs(neighbor[0] - end[0]) + abs(neighbor[1] - end[1])
                heapq.heappush(queue, (priority, neighbor, new_path))

    print(json.dumps([]))

solve()

