import json
from xml.etree import ElementTree as ET

def find_path(grid, start, end):
    queue = [(start, [])]
    visited = {start}
    while queue:
        (x, y), path = queue.pop(0)
        if (x, y) == end:
            return path
        for dx, dy, btn in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid and grid[(nx, ny)] != 'WALL' and (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [btn]))
    return None

def solve():
    root = ET.fromstring(map_xml_string)
    grid = {}
    unseen = []
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            seen = tile.attrib['seen'] == 'true'
            grid[(x, y)] = t_type
            if not seen:
                unseen.append((x, y))

    start = (input_data['current_x'], input_data['current_y'])

    reachable_unseen = []
    for ux, uy in unseen:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = ux + dx, uy + dy
            if (nx, ny) in grid and grid[(nx, ny)] != 'WALL':
                path = find_path(grid, start, (nx, ny))
                if path is not None:
                    reachable_unseen.append({"x": ux, "y": uy})
                    break

    if input_data.get('autopress_buttons', False):
        if not reachable_unseen:
            print(json.dumps([]))
            return
        reachable_unseen.sort(key=lambda p: abs(p['x']-start[0]) + abs(p['y']-start[1]))
        target_coords = (reachable_unseen[0]['x'], reachable_unseen[0]['y'])

        best_path = None
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = target_coords[0] + dx, target_coords[1] + dy
            if (nx, ny) in grid and grid[(nx, ny)] != 'WALL':
                path = find_path(grid, start, (nx, ny))
                if path is not None:
                    if best_path is None or len(path) < len(best_path):
                        best_path = path
        print(json.dumps(best_path if best_path else []))
    else:
        print(json.dumps(reachable_unseen))

solve()
