import xml.etree.ElementTree as ET
import heapq

def parse_map_xml(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = [['' for _ in range(width)] for _ in range(height)]
    objects = []
    player_pos = None
    one_way_tiles = {}

    for y, row_elem in enumerate(root.findall('Row')):
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_type = tile_elem.get('type')
            grid[y][x] = tile_type

            # FIX: Revert to broader object detection, but will filter in is_impassable
            if len(tile_elem) > 0:
                objects.append((x, y))
                if tile_elem.find('Player') is not None:
                    player_pos = (x, y)

            if 'LEDGE_HOP' in tile_type:
                if 'DOWN' in tile_type:
                    one_way_tiles[(x, y)] = (0, 1)
                elif 'LEFT' in tile_type:
                    one_way_tiles[(x, y)] = (-1, 0)
                elif 'RIGHT' in tile_type:
                    one_way_tiles[(x, y)] = (1, 0)
                elif 'UP' in tile_type:
                    one_way_tiles[(x, y)] = (0, -1)

    return grid, objects, player_pos, one_way_tiles, width, height

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(grid, node, one_way_tiles, width, height):
    neighbors = []
    x, y = node
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy
        if 0 <= nx < width and 0 <= ny < height:
            if (x, y) in one_way_tiles and (dx, dy) != one_way_tiles.get((x, y)):
                continue
            if (nx, ny) in one_way_tiles:
                ow_dx, ow_dy = one_way_tiles[(nx, ny)]
                if (dx, dy) == (-ow_dx, -ow_dy):
                    continue
            neighbors.append((nx, ny))
    return neighbors

def is_impassable(grid, pos, objects, can_surf):
    x, y = pos
    tile_type = grid[y][x]

    # FIX: If a tile is an object, check if its type is inherently traversable (like a warp)
    if pos in objects:
        traversable_object_types = ['LADDER', 'DOOR', 'CAVE', 'WARP_PANEL'] # Add other warp types as needed
        if tile_type in traversable_object_types:
            return False # It's an object, but it's a warp, so it's not impassable for pathing
        return True # It's an object and not a warp, so it's impassable

    impassable_types = ['WALL', 'COUNTER', 'PC', 'BOOKSHELF', 'TV', 'ROCK', 'CUT_TREE', 'BOULDER']
    if not can_surf:
        impassable_types.append('WATER')
    return tile_type in impassable_types

def a_star(start, end, grid, objects, one_way_tiles, width, height, can_surf):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = { (x,y): float('inf') for x in range(width) for y in range(height) }
    g_score[start] = 0
    f_score = { (x,y): float('inf') for x in range(width) for y in range(height) }
    f_score[start] = heuristic(start, end)

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            return path[::-1]

        for neighbor in get_neighbors(grid, current, one_way_tiles, width, height):
            # Temporarily remove end node from obstacles to allow pathing to it
            temp_objects = [obj for obj in objects if obj != end]
            if is_impassable(grid, neighbor, temp_objects, can_surf):
                continue

            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = g_score[neighbor] + heuristic(neighbor, end)
                heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return None

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
can_surf = input_data.get('can_surf', 'false').lower() == 'true'

grid, objects, _, one_way_tiles, width, height = parse_map_xml(map_xml_string)

start_node = (start_x, start_y)
end_node = (end_x, end_y)

path = None

impassable_tile_types = ['WALL', 'COUNTER', 'PC', 'BOOKSHELF', 'TV', 'ROCK', 'CUT_TREE', 'BOULDER']
if not can_surf:
    impassable_tile_types.append('WATER')

end_tile_type = grid[end_node[1]][end_node[0]]

if end_tile_type in impassable_tile_types:
    possible_goals = get_neighbors(grid, end_node, one_way_tiles, width, height)
    valid_adjacent_goals = [p for p in possible_goals if not is_impassable(grid, p, objects, can_surf)]

    if valid_adjacent_goals:
        new_end_node = min(valid_adjacent_goals, key=lambda p: heuristic(start_node, p))
        path = a_star(start_node, new_end_node, grid, objects, one_way_tiles, width, height, can_surf)
else:
    pathfinding_objects = [obj for obj in objects if obj != start_node]
    path = a_star(start_node, end_node, grid, pathfinding_objects, one_way_tiles, width, height, can_surf)

if path:
    directions = []
    for i in range(len(path) - 1):
        x1, y1 = path[i]
        x2, y2 = path[i+1]
        if x2 > x1: directions.append('Right')
        elif x2 < x1: directions.append('Left')
        elif y2 > y1: directions.append('Down')
        elif y2 < y1: directions.append('Up')
    print(','.join(directions))
else:
    print('No path found.')

