import xml.etree.ElementTree as ET

def parse_map_xml_for_debug(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid_types = [['' for _ in range(width)] for _ in range(height)]
    objects = []

    for y, row_elem in enumerate(root.findall('Row')):
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            grid_types[y][x] = tile_elem.get('type')
            if len(tile_elem) > 0:
                objects.append((x, y))
    return grid_types, objects, width, height

def is_impassable_for_debug(tile_type, pos, objects, can_surf):
    if pos in objects:
        return True
    impassable_types = ['WALL', 'COUNTER', 'PC', 'BOOKSHELF', 'TV', 'ROCK', 'CUT_TREE', 'BOULDER']
    if not can_surf:
        impassable_types.append('WATER')
    return tile_type in impassable_types

can_surf_arg = input_data.get('can_surf', 'false').lower() == 'true'
grid_data, object_data, map_width, map_height = parse_map_xml_for_debug(map_xml_string)

output_grid = []
for y in range(map_height):
    row_str = ''
    for x in range(map_width):
        tile_type = grid_data[y][x]
        if is_impassable_for_debug(tile_type, (x, y), object_data, can_surf_arg):
            row_str += 'X'
        else:
            row_str += '.'
    output_grid.append(row_str)

for row in output_grid:
    print(row)
