import xml.etree.ElementTree as ET
from collections import deque
import json

def run():
    try:
        root = ET.fromstring(map_xml_string)
        player_pos = None
        grid = {}
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                if tile.find('Player') is not None:
                    player_pos = (x, y)
                grid[(x, y)] = {
                    'type': tile.attrib['type'],
                    'seen': tile.attrib['seen'] == 'true',
                    'has_object': tile.attrib.get('has-object') == 'true',
                    'is_warp': tile.attrib.get('is-warp') == 'true'
                }

        if not player_pos:
            print("Error: Player pos not found.")
            return

        passable_types = ['FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'WARP_CARPET', 'WATER', 'ICE', 'LADDER', 'PIT']

        reachable = set()
        queue = deque([player_pos])
        visited = {player_pos}

        while queue:
            cx, cy = queue.popleft()
            reachable.add((cx, cy))
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy
                if (nx, ny) in grid and (nx, ny) not in visited:
                    tile = grid[(nx, ny)]
                    if tile['seen'] and tile['type'] in passable_types and not tile['has_object'] and not tile['is_warp']:
                        visited.add((nx, ny))
                        queue.append((nx, ny))

        unseen_reachable = set()
        for rx, ry in reachable:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = rx + dx, ry + dy
                if (nx, ny) in grid and not grid[(nx, ny)]['seen']:
                    unseen_reachable.add((nx, ny))

        print(json.dumps(sorted(list(unseen_reachable))))
    except Exception as e:
        print(f"Error: {str(e)}")

run()

