import xml.etree.ElementTree as ET
import json
import collections

def get_map_data(xml_str):
    root = ET.fromstring(xml_str)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    grid = {}
    player_pos = None
    boulders = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            grid[(x, y)] = t_type
            if tile.find('Player') is not None:
                player_pos = (x, y)
            obj = tile.find('Object')
            if obj is not None and obj.attrib.get('id-name') == 'BOULDER':
                boulders[int(obj.attrib['id'])] = (x, y)
    return grid, width, height, player_pos, boulders

def get_path(grid, start, end, obstacles):
    queue = collections.deque([(start, [])])
    visited = {start}
    while queue:
        (x, y), path = queue.popleft()
        if (x, y) == end:
            return path
        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if (nx, ny) not in visited and grid.get((nx, ny)) in ['FLOOR', 'ICE'] and (nx, ny) not in obstacles:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [btn]))
    return None

def solve():
    try:
        # Use input_data from global scope
        mode = input_data.get('mode', 'path')
        grid, w, h, p_pos, boulders = get_map_data(map_xml_string)
        if mode == 'path':
            tx, ty = int(input_data['target_x']), int(input_data['target_y'])
            obstacles = set(boulders.values())
            path = get_path(grid, p_pos, (tx, ty), obstacles)
            print(json.dumps(path if path else []))
        elif mode == 'boulder':
            bid = input_data.get('boulder_id')
            if bid is not None:
                bx, by = boulders.get(int(bid))
            else:
                bx, by = int(input_data['boulder_x']), int(input_data['boulder_y'])
            tx, ty = int(input_data['target_x']), int(input_data['target_y'])
            queue = collections.deque([( (bx, by), p_pos, [] )])
            visited = { ((bx, by), p_pos) }
            while queue:
                (cbx, cby), (cpx, cpy), full_path = queue.popleft()
                if (cbx, cby) == (tx, ty):
                    print(json.dumps(full_path))
                    return
                for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                    push_pos = (cbx - dx, cby - dy)
                    next_pos = (cbx + dx, cby + dy)
                    if grid.get(push_pos) in ['FLOOR', 'ICE'] and grid.get(next_pos) in ['FLOOR', 'ICE', 'PIT']:
                        other_boulders = {pos for id, pos in boulders.items() if id != bid}
                        p_path = get_path(grid, (cpx, cpy), push_pos, other_boulders | {(cbx, cby)})
                        if p_path is not None:
                            if grid.get(next_pos) == 'PIT':
                                print(json.dumps(full_path + p_path + [btn]))
                                return
                            new_state = (next_pos, (cbx, cby))
                            if new_state not in visited:
                                visited.add(new_state)
                                queue.append((next_pos, (cbx, cby), full_path + p_path + [btn]))
            print("[]")
    except Exception as e:
        print(json.dumps([f"Error: {str(e)}"]))
solve()

