import xml.etree.ElementTree as ET
from collections import deque
import json

def run():
    xml_str = map_xml_string
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    extra_obstacles = input_data.get('extra_obstacles', [])

    root = ET.fromstring(xml_str)
    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            grid[(x, y)] = tile.attrib['type']
            if tile.attrib.get('has-object') == 'true':
                 grid[(x, y)] = 'WALL'
            if tile.attrib['seen'] == 'false':
                 grid[(x, y)] = 'WALL'

    for obs in extra_obstacles:
        grid[(int(obs['x']), int(obs['y']))] = 'WALL'

    queue = deque([((start_x, start_y), [])])
    visited = {(start_x, start_y)}

    while queue:
        (curr_x, curr_y), path = queue.popleft()
        if (curr_x, curr_y) == (target_x, target_y):
            print(json.dumps(path))
            return

        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = curr_x, curr_y
            while True:
                tx, ty = nx + dx, ny + dy
                if (tx, ty) not in grid or grid[(tx, ty)] == 'WALL':
                    break
                nx, ny = tx, ty
                if grid[(nx, ny)] != 'ICE':
                    break

            if (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [move]))
    print(json.dumps([]))

run()
