inventory_scanner.python_script:
import json

try:
    inventory_list = json.loads(input_data['inventory_list'])
    target_item = input_data['target_item']
    found = False
    for item in inventory_list:
        if target_item.lower() in item.lower():
            found = True
            break
    print(f'Item found: {found}')
except Exception as e:
    print(f'An error occurred: {e}')

pathfinder.python_script:
import xml.etree.ElementTree as ET
print('--- Pathfinder Test 3: Player Position Finding ---')
try:
    root = ET.fromstring(map_xml_string)
    player_pos = None
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.get('has-player') == 'true':
                player_pos = (int(tile_elem.get('id')), int(row_elem.get('id')))
                break
        if player_pos:
            break
    if player_pos:
        print(f'SUCCESS: Player found at {player_pos}')
    else:
        print('FAILURE: Player not found after loop.')
except Exception as e:
    print(f'FAILURE: Error during player search: {e}')
print('--- End Test 3 ---')
