import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path(start_x, start_y, target_x, target_y, output_format=None, autopress_buttons=False):
    # Smart default for output_format
    if output_format is None:
        if autopress_buttons:
            output_format = "buttons"
        else:
            output_format = "coords"

    try:
        root = ET.fromstring(map_xml_string)
    except Exception as e:
        print(f"Error parsing XML: {e}")
        return []

    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = [[{'type': 'unseen', 'is_warp': False, 'has_object': False} for _ in range(height)] for _ in range(width)]

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib.get('type', 'unseen')
            is_warp = tile.attrib.get('is-warp', 'false') == 'true'

            # Check for objects that block movement
            has_object = False
            if tile.findall('Object'):
                has_object = True
            if tile.attrib.get('has-object', 'false') == 'true':
                 has_object = True

            # Check for "Shutter Closed" markers in <Marker> tags inside the tile
            # This is a heuristic to treat closed shutters as walls
            for marker in tile.findall('Marker'):
                label = marker.text or ""
                if "Shutter Closed" in label:
                    tile_type = "WALL" 

            grid[x][y] = {
                'type': tile_type,
                'is_warp': is_warp,
                'has_object': has_object
            }

    # BFS
    queue = deque([[(start_x, start_y)]])
    visited = set([(start_x, start_y)])

    directions = [(0, -1), (0, 1), (-1, 0), (1, 0)] # Up, Down, Left, Right

    found_path = None

    while queue:
        path = queue.popleft()
        curr_x, curr_y = path[-1]

        if curr_x == target_x and curr_y == target_y:
            found_path = path
            break

        for dx, dy in directions:
            nx, ny = curr_x + dx, curr_y + dy

            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited:
                    cell = grid[nx][ny]

                    # Logic: walkable if not WALL/unseen and not blocking object
                    # Exception: If it is the *target* tile, we allow it even if it has an object
                    # (assuming we want to interact with it). 
                    # Note: We still can't walk *through* walls to get there.

                    is_walkable_tile = cell['type'] not in ['WALL', 'unseen']
                    is_blocked_by_obj = cell['has_object']

                    if is_walkable_tile and (not is_blocked_by_obj or (nx == target_x and ny == target_y)):
                        visited.add((nx, ny))
                        new_path = list(path)
                        new_path.append((nx, ny))
                        queue.append(new_path)

    if found_path:
        if output_format == "buttons":
            buttons = []
            for i in range(len(found_path) - 1):
                cx, cy = found_path[i]
                nx, ny = found_path[i+1]
                if nx > cx: buttons.append("Right")
                elif nx < cx: buttons.append("Left")
                elif ny > cy: buttons.append("Down")
                elif ny < cy: buttons.append("Up")
            print(json.dumps(buttons))
        else:
            json_path = [{"x": x, "y": y} for x, y in found_path]
            print(json.dumps(json_path))
    else:
        print("[]")

# Extract arguments
start_x = input_data.get('start_x')
start_y = input_data.get('start_y')
target_x = input_data.get('target_x')
target_y = input_data.get('target_y')
output_format = input_data.get('output_format')
autopress_buttons = input_data.get('autopress_buttons', False)

find_path(start_x, start_y, target_x, target_y, output_format, autopress_buttons)
