import json
import sys

def main():
    try:
        raw_input = sys.stdin.read()
        if not raw_input:
            print(json.dumps([]))
            return
        input_data = json.loads(raw_input)
        sequence = input_data.get('sequence', [])
        if not isinstance(sequence, list):
            sequence = []
        print(json.dumps(sequence))
    except Exception as e:
        print(json.dumps([]))

if __name__ == "__main__":
    main()
