import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path(map_xml_string, target_x, target_y, input_data):
    try:
        root = ET.fromstring(map_xml_string)
    except Exception as e:
        return {"error": f"Failed to parse map XML: {str(e)}"}

    grid = {}
    player_pos = None
    player_start_on_water = False
    avoid_labels = input_data.get('avoid_labels', ['rock', 'blocked'])
    treat_unseen_as_traversable = input_data.get('treat_unseen_as_traversable', True)

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            seen = tile.attrib['seen'] == 'true'
            has_object = tile.attrib.get('has-object') == 'true'

            if tile.find('Player') is not None:
                player_pos = (x, y)
                if t_type == 'WATER':
                    player_start_on_water = True

            if not seen:
                if treat_unseen_as_traversable:
                    grid[(x, y)] = 'FLOOR'
                else:
                    grid[(x, y)] = 'WALL'
                continue

            is_blocked_by_marker = False
            for marker in tile.findall('Marker'):
                label = marker.text.lower()
                if any(word in label for word in avoid_labels):
                    is_blocked_by_marker = True

            if t_type in ['WALL', 'BUOY', 'DOOR'] or has_object or is_blocked_by_marker:
                grid[(x, y)] = 'WALL'
            elif t_type == 'WATER':
                grid[(x, y)] = 'WATER'
            elif t_type == 'LEDGE_HOP_DOWN':
                grid[(x, y)] = 'LEDGE'
            elif t_type == 'FLOOR_UP_WALL':
                grid[(x, y)] = 'UP_WALL'
            else:
                grid[(x, y)] = 'FLOOR'

    if player_pos is None:
        return {"error": "Player position not found in XML"}

    goal = (target_x, target_y)

    # BFS state: (x, y), surfing, path
    queue = deque([(player_pos, player_start_on_water, [])])
    visited = {(player_pos, player_start_on_water)}

    while queue:
        (x, y), surfing, path = queue.popleft()

        if (x, y) == goal:
            return path[:50]

        if len(path) >= 250: continue

        for dx, dy, action in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if (nx, ny) not in grid or grid[(nx, ny)] == 'WALL': continue

            target = grid[(nx, ny)]
            new_path = list(path)

            if not surfing and target == 'WATER':
                new_path.extend([action, 'A', 'sleep 500', 'A', 'sleep 500', 'A'])
                new_surfing = True
            elif surfing and target != 'WATER':
                new_path.append(action)
                new_surfing = False
            else:
                new_path.append(action)
                new_surfing = surfing

            if target == 'LEDGE' and action != 'Down': continue
            if target == 'UP_WALL' and action == 'Down': continue

            if (nx, ny, new_surfing) not in visited:
                visited.add((nx, ny, new_surfing))
                queue.append(((nx, ny), new_surfing, new_path))

    return []

try:
    tx = int(input_data['target_x'])
    ty = int(input_data['target_y'])
    result = find_path(map_xml_string, tx, ty, input_data)
    print(json.dumps(result))
except Exception as e:
    print(json.dumps({"error": str(e)}))

