import json
import xml.etree.ElementTree as ET
from collections import deque
import sys

def find_path(xml_str, start_x, start_y, target_x, target_y, ignore_objects=False, allow_water=False):
    try:
        root = ET.fromstring(xml_str)
    except Exception:
        return []

    grid = {}
    traversable_types = ['FLOOR', 'DOOR', 'LEDGE_HOP_DOWN', 'FLOOR_UP_WALL', 'WARP_CARPET_DOWN', 'unseen']
    if allow_water:
        traversable_types.append('WATER')

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib['type']
            has_object = tile.attrib.get('has-object') == 'true'
            traversable = tile_type in traversable_types
            if not ignore_objects and has_object:
                traversable = False
            grid[(x, y)] = (traversable, tile_type)

    queue = deque([(start_x, start_y, [])])
    visited = set([(start_x, start_y)])

    while queue:
        cx, cy, path = queue.popleft()
        if (cx, cy) == (target_x, target_y):
            return path

        _, curr_ttype = grid.get((cx, cy), (False, 'WALL'))

        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in grid:
                traversable, target_ttype = grid[(nx, ny)]
                can_move = False
                if traversable:
                    # Entering target tile
                    if target_ttype == 'LEDGE_HOP_DOWN':
                        if move == 'Down': can_move = True
                    elif target_ttype == 'FLOOR_UP_WALL':
                        if move == 'Up': can_move = True
                    else:
                        can_move = True

                    # Leaving current tile
                    if curr_ttype == 'LEDGE_HOP_DOWN' and move != 'Down':
                        can_move = False
                    if curr_ttype == 'FLOOR_UP_WALL' and move not in ['Up', 'Down']:
                        can_move = False

                if can_move and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [move]))
    return []

raw_input = sys.stdin.read()
if not raw_input:
    print(json.dumps([]))
    sys.exit(0)

try:
    input_data = json.loads(raw_input)
    path = find_path(map_xml_string, int(input_data['start_x']), int(input_data['start_y']), int(input_data['target_x']), int(input_data['target_y']), input_data.get('ignore_objects', False), input_data.get('allow_water', False))
    print(json.dumps(path))
except Exception:
    print(json.dumps([]))
